  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'configuration',
	title: 'Testrunner Configuration',
	sidebar_label: 'Configuration'
};
export const metadata = {
  "unversionedId": "dev/testrunner-toolkit/configuration",
  "id": "dev/testrunner-toolkit/configuration",
  "isDocsHomePage": false,
  "title": "Testrunner Configuration",
  "description": "The Testrunner Toolkit requires a configuration file to know which tests to run, along with which framework to use. Examples of some possible configuration fields you can set include:",
  "source": "@site/docs/dev/testrunner-toolkit/configuration.md",
  "slug": "/dev/testrunner-toolkit/configuration",
  "permalink": "/dev/testrunner-toolkit/configuration",
  "editUrl": "https://github.com/saucelabs/sauce-docs/edit/master/docs/dev/testrunner-toolkit/configuration.md",
  "version": "current",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar_label": "Configuration",
  "sidebar": "someSidebar",
  "previous": {
    "title": "Testrunner Installation",
    "permalink": "/dev/testrunner-toolkit/installation"
  },
