  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'mobile-apps',
	title: 'Live Testing Mobile Apps',
	sidebar_label: 'Mobile Apps'
};
export const metadata = {
  "unversionedId": "guides/live-testing/mobile-apps",
  "id": "guides/live-testing/mobile-apps",
  "isDocsHomePage": false,
  "title": "Live Testing Mobile Apps",
  "description": "With Virtual Devices on Sauce Labs, you can test your mobile applications on a variety of Android emulators and iOS simulators. If you do not have an app, consider using the Sauce Labs Swag Labs sample app for validating your account functionality as well as your tests.",
  "source": "@site/docs/guides/live-testing/mobile-apps.md",
  "permalink": "/guides/live-testing/mobile-apps",
