export const rightToc = [{
  value: 'What You&#39;ll Need',
  id: 'what-youll-need',
  children: []
}, {
  value: 'Upload Your Application',
  id: 'upload-your-application',
  children: []
}, {
  value: 'Virtual Device Testing',
  id: 'virtual-device-testing',
  children: [{
    value: 'Starting a Session',
    id: 'starting-a-session',
    children: []
  }, {
    value: 'Selecting a Device',
    id: 'selecting-a-device',
    children: []
  }, {
    value: 'Launching a Device',
    id: 'launching-a-device',
    children: []
  }]
}, {
  value: 'Real Device Testing',
  id: 'real-device-testing',
  children: [{
    value: 'Starting a Session',
    id: 'starting-a-session-1',
    children: []
  }, {
    value: 'Selecting a Device',
    id: 'selecting-a-device-1',
    children: []
  }, {
    value: 'Launching a Device',
    id: 'launching-a-device-1',
    children: []
  }]
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`With Virtual Devices on Sauce Labs, you can test your mobile applications on a variety of Android emulators and iOS simulators. If you do not have an app, consider using the Sauce Labs `}<a parentName="p" {...{
        "href": "https://github.com/saucelabs/sample-app-mobile"
      }}>{`Swag Labs`}</a>{` sample app for validating your account functionality as well as your tests. `}</p>
    <h2 {...{
      "id": "what-youll-need"
    }}>{`What You'll Need`}</h2>
    <ul>
      <li parentName="ul">{`A Sauce Labs Account`}</li>
      <li parentName="ul">{`A Mobile Application to Test`}<blockquote parentName="li">
          <p parentName="blockquote">{`If you do not have an app, consider using the Sauce Labs `}<a parentName="p" {...{
              "href": "https://github.com/saucelabs/sample-app-mobile"
            }}>{`Swag Labs`}</a>{` sample app for validating your account functionality as well as your tests.`}</p>
        </blockquote></li>
    </ul>
    <h2 {...{
      "id": "upload-your-application"
    }}>{`Upload Your Application`}</h2>
    <p>{`Before you begin a live test session, you must upload an application to `}<a parentName="p" {...{
        "href": "/storage"
      }}>{`Sauce Application Storage`}</a>{`.`}</p>
    <p>{`For further information regarding:`}</p>
    <ul>
      <li parentName="ul">{`managing your application build/version.`}</li>
      <li parentName="ul">{`modifying application startup settings.`}</li>
      <li parentName="ul">{`viewing the storage API reference.`}</li>
    </ul>
    <p>{`Visit the `}<a parentName="p" {...{
        "href": "/storage"
      }}>{`Sauce Labs Application Storage`}</a>{` page.`}</p>
    <h2 {...{
      "id": "virtual-device-testing"
    }}>{`Virtual Device Testing`}</h2>
    <h3 {...{
      "id": "starting-a-session"
    }}>{`Starting a Session`}</h3>
    <p>{`After you've uploaded and selected the application, you're ready to launch a live testing session. `}</p>
    <h3 {...{
      "id": "selecting-a-device"
    }}>{`Selecting a Device`}</h3>
    <h3 {...{
      "id": "launching-a-device"
    }}>{`Launching a Device`}</h3>
    <h2 {...{
      "id": "real-device-testing"
    }}>{`Real Device Testing`}</h2>
    <h3 {...{
      "id": "starting-a-session-1"
    }}>{`Starting a Session`}</h3>
    <p>{`After you've uploaded and selected the application, you're ready to launch a live testing session. `}</p>
    <h3 {...{
      "id": "selecting-a-device-1"
    }}>{`Selecting a Device`}</h3>
    <p>{`You must select a device prior to launching a session. By default, the public devices will display in a grid format like so:
`}<img alt={"Device Grid"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./../../assets/device-grid.png").default} /></p>
    <p>{`There is a distinction between `}<strong parentName="p">{`Public Devices`}</strong>{` and `}<strong parentName="p">{`Private Devices`}</strong>{`.`}</p>
    <ul>
      <li parentName="ul">{`Public devices are accessible by anyone with a Sauce Labs Account and are subject to availability. If a device is in use, you will see a yellow "Busy" flag across the device thumbnail.`}</li>
      <li parentName="ul">{`Private devices are only accessible by anyone in your team or org account. Private devices are an enterprise only feature.`}</li>
    </ul>
    <h3 {...{
      "id": "launching-a-device-1"
    }}>{`Launching a Device`}</h3>
    <p>{`Hover over a device thumbnail and click on `}<strong parentName="p">{`Details`}</strong>{` to see the specs for that device. Once you find the desired device, select `}<strong parentName="p">{`Launch`}</strong>{`.
`}<img alt={"Device Details"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./../../assets/device-details.png").default} /></p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
