  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'web-apps',
	title: 'Live Testing Web Apps',
	sidebar_label: 'Web Apps'
};
export const metadata = {
  "unversionedId": "guides/live-testing/web-apps",
  "id": "guides/live-testing/web-apps",
  "isDocsHomePage": false,
  "title": "Live Testing Web Apps",
  "description": "You can run live tests of your websites/web-applications on a wide range of operating systems and web browser versions.",
  "source": "@site/docs/guides/live-testing/web-apps.md",
  "permalink": "/guides/live-testing/web-apps",
  "editUrl": "https://github.com/saucelabs/sauce-docs/edit/master/docs/guides/live-testing/web-apps.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar_label": "Web Apps",
  "sidebar": "docs",
  "previous": {
    "title": "Live Testing Overview",
    "permalink": "/guides/live-testing/overview"
  },
  "next": {
    "title": "Live Testing Mobile Apps",
    "permalink": "/guides/live-testing/mobile-apps"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'What You&#39;ll Need',
  id: 'what-youll-need',
  children: []
}, {
  value: 'Testing on Desktop Browsers',
  id: 'testing-on-desktop-browsers',
  children: [{
    value: 'Running Tests in Parallel',
    id: 'running-tests-in-parallel',
    children: []
  }]
}, {
  value: 'Testing on Mobile Browsers',
  id: 'testing-on-mobile-browsers',
  children: [{
    value: 'Real Devices',
    id: 'real-devices',
    children: []
  }, {
    value: 'Virtual Devices',
    id: 'virtual-devices',
    children: []
  }]
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`You can run live tests of your websites/web-applications on a wide range of operating systems and web browser versions.`}</p>
    <h2 {...{
      "id": "what-youll-need"
    }}>{`What You'll Need`}</h2>
    <ul>
      <li parentName="ul">{`A Sauce Labs Account`}</li>
      <li parentName="ul">{`A Publicly Available Web Application to Test`}<blockquote parentName="li">
          <p parentName="blockquote">{`If you do not have an app, consider using the Sauce Labs `}<a parentName="p" {...{
              "href": "https://www.saucedemo.com/"
            }}>{`Swag Labs`}</a>{` sample app for validating your account functionality as well as your tests.`}</p>
        </blockquote></li>
    </ul>
    <h2 {...{
      "id": "testing-on-desktop-browsers"
    }}>{`Testing on Desktop Browsers`}</h2>
    <ol>
      <li parentName="ol">{`Log in to Sauce Labs and go to `}<strong parentName="li">{`Live > Cross-Browser`}</strong>{`. `}</li>
      <li parentName="ol">{`Click `}<strong parentName="li">{`Web Browser`}</strong>{`.`}</li>
      <li parentName="ol">{`Click `}<strong parentName="li">{`Desktop`}</strong>{`.`}</li>
      <li parentName="ol">{`For `}<strong parentName="li">{`Test URL`}</strong>{`, enter the URL of the website you want to test in the browser.`}</li>
      <li parentName="ol">{`Select the `}<strong parentName="li">{`Browser Version`}</strong>{` and `}<strong parentName="li">{`Screen Resolution`}</strong>{` you want to use in your test. `}</li>
      <li parentName="ol">{`Select the `}<strong parentName="li">{`OS Version`}</strong>{` you want to use. `}</li>
      <li parentName="ol">{`You can find previous configurations you've used in your tests under `}<strong parentName="li">{`Recent`}</strong>{`. You can also save configurations for later use by selecting `}<strong parentName="li">{`Save this Configuration`}</strong>{`. `}</li>
      <li parentName="ol">{`If you use `}<a parentName="li" {...{
          "href": "https://wiki.saucelabs.com/display/DOCS/Sauce+Connect+Proxy"
        }}>{`Sauce Connect Proxy`}</a>{` to test application on local host or behind a firewall, select the tunnel to use for your tests.`}</li>
      <li parentName="ol">{`Test assets such as screenshots are automatically saved. If you don't want to save them, clear the `}<strong parentName="li">{`Save Screenshots, Logs, & Video`}</strong>{` option. `}</li>
      <li parentName="ol">{`Click `}<strong parentName="li">{`Start Session`}</strong>{`.`}</li>
    </ol>
    <p>{`You'll see a loading screen, and then the application will launch in a live test window using the configuration you selected. Once your session launches, you can use your mouse cursor and keyboard to interact with the website under test.`}</p>
    <h3 {...{
      "id": "running-tests-in-parallel"
    }}>{`Running Tests in Parallel`}</h3>
    <p>{`You can run multiple live test sessions at the same time, with the number of tests limited by the concurrency allowance associated with your account. If you want to start additional sessions, click the `}<strong parentName="p">{`+`}</strong>{` icon next to the tab containing the URL of your current test session. Follow the steps to set up the session, and then you can switch back and forth between the sessions by clicking on the URL tabs.`}</p>
    <h2 {...{
      "id": "testing-on-mobile-browsers"
    }}>{`Testing on Mobile Browsers`}</h2>
    <h3 {...{
      "id": "real-devices"
    }}>{`Real Devices`}</h3>
    <ol>
      <li parentName="ol">{`Log in to Sauce Labs and go to `}<strong parentName="li">{`Live > Cross-Browser`}</strong>{` from the options in the left-hand navigation. `}</li>
      <li parentName="ol">{`Select `}<strong parentName="li">{`Mobile Real`}</strong>{`.`}</li>
      <li parentName="ol">{`Use the filter options or `}<strong parentName="li">{`Search`}</strong>{` field to find the type of real device you want to use in your test, and select it. You can find previous devices you've used in your tests under `}<strong parentName="li">{`Recent`}</strong>{`. You can also save devices for later use by selecting `}<strong parentName="li">{`Save this Configuration`}</strong>{`. `}</li>
      <li parentName="ol">{`For `}<strong parentName="li">{`Test URL`}</strong>{`, enter the URL of the website you want to test in the browser.`}</li>
      <li parentName="ol">{`If you are using `}<strong parentName="li">{`Sauce Connect Proxy`}</strong>{` to access the web app you want to test, enter the ID of the tunnel to use. `}</li>
      <li parentName="ol">{`Click `}<strong parentName="li">{`Start Session`}</strong>{`. `}</li>
    </ol>
    <p>{`You'll see a loading screen, and then the URL you entered will launch in a live test window using the real device you selected.`}</p>
    <h3 {...{
      "id": "virtual-devices"
    }}>{`Virtual Devices`}</h3>
    <ol>
      <li parentName="ol">{`Log in to Sauce Labs and select `}<strong parentName="li">{`Live > Cross Browser`}</strong>{` from the options in the left-hand navigation. `}</li>
      <li parentName="ol">{`Select `}<strong parentName="li">{`Mobile Virtual`}</strong>{`.`}</li>
      <li parentName="ol">{`Select the specifications for the virtual device you want to use from the `}<strong parentName="li">{`Manufacturer`}</strong>{`, `}<strong parentName="li">{`Device`}</strong>{`, and `}<strong parentName="li">{`OS Version`}</strong>{` menus. `}</li>
      <li parentName="ol">{`For `}<strong parentName="li">{`Test URL`}</strong>{`, enter the URL of the website you want to test in the browser.`}</li>
      <li parentName="ol">{`If you are using `}<strong parentName="li">{`Sauce Connect Proxy`}</strong>{` to access the web app you want to test, enter the ID of the tunnel to use. `}</li>
      <li parentName="ol">{`Click `}<strong parentName="li">{`Start Session`}</strong>{`.`}</li>
    </ol>
    <p>{`You'll see a loading screen, and then the URL you entered will launch in a live test window using the virtual device you selected.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
