  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'circleci',
	title: 'Testrunner Toolkit with CircleCI',
	sidebar_label: 'CircleCI'
};
export const metadata = {
  "unversionedId": "testrunner-toolkit/integrations/circleci",
  "id": "testrunner-toolkit/integrations/circleci",
  "isDocsHomePage": false,
  "title": "Testrunner Toolkit with CircleCI",
  "description": "These examples can apply to virtually any CirceCI deployment, provided that you already have some existing automated tests, and are either the maintainer or an admin of the target repository.",
  "source": "@site/docs/testrunner-toolkit/integrations/circleci.md",
  "slug": "/testrunner-toolkit/integrations/circleci",
  "permalink": "/testrunner-toolkit/integrations/circleci",
  "editUrl": "https://github.com/saucelabs/sauce-docs/edit/master/docs/testrunner-toolkit/integrations/circleci.md",
  "version": "current",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar_label": "CircleCI"
};
  /* @jsxRuntime classic */
/* @jsx mdx */
import useBaseUrl from '@docusaurus/useBaseUrl';
import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
export const toc = [{
  value: 'What You&#39;ll Need',
  id: 'what-youll-need',
  children: []
}, {
  value: 'Project Setup',
  id: 'project-setup',
  children: []
}, {
  value: 'Add Project Environment Variables',
  id: 'add-project-environment-variables',
  children: []
}, {
  value: 'Modify the CirceCI Configuration',
  id: 'modify-the-circeci-configuration',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`These examples can apply to virtually any CirceCI deployment, provided that you already have some existing automated tests, and are either the maintainer or an admin of the target repository.`}</p>
    <h2 {...{
      "id": "what-youll-need"
    }}>{`What You'll Need`}</h2>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://circleci.com/signup/"
        }}>{`CirceCI Account`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://saucelabs.com/sign-up"
        }}>{`SauceLabs Account`}</a></li>
      <li parentName="ul">{`A git repository hosting service (GitHub or BitBucket)`}</li>
    </ul>
    <h2 {...{
      "id": "project-setup"
    }}>{`Project Setup`}</h2>
    <p>{`The first step is to ensure you have a CircleCI account, and to login with your git hosting provider username; the examples below use GitHub authentication.`}</p>
    <ol>
      <li parentName="ol">{`Log in to CircleCI`}</li>
      <li parentName="ol">{`Choose the desired repo and click "Set Up Project"`}</li>
      <li parentName="ol">{`Select Add Config (or Use Existing Config). This creates a new branch in your project called `}<inlineCode parentName="li">{`circle-ci-project-setup`}</inlineCode></li>
    </ol>
    <blockquote>
      <p parentName="blockquote">{`Do not worry if your project fails to build. You need to modify the `}<inlineCode parentName="p">{`config.yml`}</inlineCode>{` manually anyway.`}</p>
    </blockquote>
    <h2 {...{
      "id": "add-project-environment-variables"
    }}>{`Add Project Environment Variables`}</h2>
    <p>{`In order for CirceCi to communicate with Sauce Labs you need to authenticate with project environment variables.`}</p>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`In CirceCI, go to your `}<strong parentName="p">{`Project Settings`}</strong></p>
        <img src={useBaseUrl('img/stt/circleci-project-settings.png')} alt="CircleCI Project Settings" width="200" />
      </li>
      <li parentName="ol">
        <p parentName="li">{`Select `}<strong parentName="p">{`Environment Variables`}</strong></p>
        <img src={useBaseUrl('img/stt/circleci-add-variables.png')} alt="Add Variables in CircleCI" width="200" />
      </li>
      <li parentName="ol">
        <p parentName="li">{`Add variables for your `}<a parentName="p" {...{
            "href": "https://app.saucelabs.com/user-settings"
          }}>{`Sauce Labs account credentials`}</a>{` as `}<inlineCode parentName="p">{`SAUCE_USERNAME`}</inlineCode>{` and `}<inlineCode parentName="p">{`SAUCE_ACCESS_KEY`}</inlineCode>{` respectively`}</p>
        <img src={useBaseUrl('img/stt/circleci-variables.png')} alt="CircleCI Variables" width="600" />
      </li>
    </ol>
    <h2 {...{
      "id": "modify-the-circeci-configuration"
    }}>{`Modify the CirceCI Configuration`}</h2>
    <p>{`In the root of your project directory, create the `}<inlineCode parentName="p">{`.circleci`}</inlineCode>{` directory if it doesn't already exist, and open/create `}<inlineCode parentName="p">{`config.yml`}</inlineCode>{`. Below are some job snippets of how to configure Testrunner Toolkit with CircleCI:`}</p>
    <Tabs defaultValue="cypress" values={[{
      label: 'Cypress',
      value: 'cypress'
    }, {
      label: 'Playwright',
      value: 'playwright'
    }, {
      label: 'TestCafe',
      value: 'testcafe'
    }]} mdxType="Tabs">

      <TabItem value="cypress" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-yaml",
            "metastring": "reference",
            "reference": true
          }}>{`https://github.com/saucelabs/testrunner-toolkit/blob/master/.circleci/config.yml#L43-L58
`}</code></pre>
      </TabItem>
      <TabItem value="playwright" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-yaml",
            "metastring": "reference",
            "reference": true
          }}>{`https://github.com/saucelabs/testrunner-toolkit/blob/master/.github/workflows/tests.yml#L60-L76
`}</code></pre>
      </TabItem>
      <TabItem value="testcafe" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-yaml",
            "metastring": "reference",
            "reference": true
          }}>{`https://github.com/saucelabs/testrunner-toolkit/blob/master/.circleci/config.yml#L96-L112
`}</code></pre>
      </TabItem>
    </Tabs>
    <blockquote>
      <p parentName="blockquote">{`You can reference the complete `}<inlineCode parentName="p">{`config.yml`}</inlineCode>{` `}<a parentName="p" {...{
          "href": "https://github.com/saucelabs/testrunner-toolkit/blob/master/.circleci/config.yml"
        }}>{`here`}</a>{`.`}</p>
    </blockquote>
    <p>{`Commit the updated `}<inlineCode parentName="p">{`config.yml`}</inlineCode>{` to your git hosting service provider. Navigate back to the CirceCI dashboard to see your build pass.`}</p>
    <img src={useBaseUrl('img/stt/circleci-output.png')} alt="CircleCI Output" />
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
