  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'jenkins',
	title: 'Testrunner Toolkit with Jenkins',
	sidebar_label: 'Jenkins'
};
export const metadata = {
  "unversionedId": "testrunner-toolkit/integrations/jenkins",
  "id": "testrunner-toolkit/integrations/jenkins",
  "isDocsHomePage": false,
  "title": "Testrunner Toolkit with Jenkins",
  "description": "These examples can apply to virtually any Jenkins deployment, provided that you already have some existing automated tests, have access to the Jenkins instance, and are either the maintainer or an admin of the target repository.",
  "source": "@site/docs/testrunner-toolkit/integrations/jenkins.md",
  "slug": "/testrunner-toolkit/integrations/jenkins",
  "permalink": "/testrunner-toolkit/integrations/jenkins",
  "editUrl": "https://github.com/saucelabs/sauce-docs/edit/master/docs/testrunner-toolkit/integrations/jenkins.md",
  "version": "current",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar_label": "Jenkins",
  "sidebar": "someSidebar",
  "previous": {
    "title": "Testrunner Toolkit CI Requirements",
    "permalink": "/testrunner-toolkit/integrations"
  },
  "next": {
    "title": "Testrunner Toolkit with GitHub Actions",
    "permalink": "/testrunner-toolkit/integrations/github-actions"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */
import useBaseUrl from '@docusaurus/useBaseUrl';
import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
export const toc = [{
  value: 'What You&#39;ll Need',
  id: 'what-youll-need',
  children: [{
    value: 'Configure Jenkins Credentials',
    id: 'configure-jenkins-credentials',
    children: []
  }, {
    value: 'Configure the Jenkins Pipeline',
    id: 'configure-the-jenkins-pipeline',
    children: []
  }]
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`These examples can apply to virtually any Jenkins deployment, provided that you already have some existing automated tests, have access to the Jenkins instance, and are either the maintainer or an admin of the target repository.`}</p>
    <h2 {...{
      "id": "what-youll-need"
    }}>{`What You'll Need`}</h2>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://www.jenkins.io/doc/book/installing/"
        }}>{`Jenkins Server`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://saucelabs.com/sign-up"
        }}>{`Sauce Labs Account`}</a></li>
      <li parentName="ul">{`The following permissions in Jenkins:`}
        <ul parentName="li">
          <li parentName="ul">{`ability to create and manage credentials`}</li>
          <li parentName="ul">{`ability to create and manage new pipelines`}</li>
        </ul>
      </li>
    </ul>
    <h3 {...{
      "id": "configure-jenkins-credentials"
    }}>{`Configure Jenkins Credentials`}</h3>
    <p>{`The first step of the integration is to ensure you've added your `}<inlineCode parentName="p">{`SAUCE_USERNAME`}</inlineCode>{` and `}<inlineCode parentName="p">{`SAUCE_ACCESS_KEY`}</inlineCode>{` as a secret file/text in your Jenkins server (Not sure where to find these? They're `}<a parentName="p" {...{
        "href": "https://app.saucelabs.com/user-settings"
      }}>{`here`}</a>{`).`}</p>
    <p>{`The easiest way to add credentials to Jenkins is with the UI:`}</p>
    <ul>
      <li parentName="ul">
        <p parentName="li">{`Log in to Jenkins`}</p>
      </li>
      <li parentName="ul">
        <p parentName="li">{`Go to `}<strong parentName="p">{`Manage Jenkins > Manage Credentials`}</strong></p>
      </li>
      <li parentName="ul">
        <p parentName="li">{`Next to (Global), select `}<strong parentName="p">{`Add credentials`}</strong></p>
  <img src={useBaseUrl('img/stt/add_credentials.png')} alt="Add Jenkins Credentials" width="500" />
      </li>
      <li parentName="ul">
        <p parentName="li">{`For `}<strong parentName="p">{`Kind`}</strong>{`, select `}<strong parentName="p">{`Secret Text`}</strong></p>
      </li>
      <li parentName="ul">
        <p parentName="li">{`Enter the following information:`}</p>
        <ul parentName="li">
          <li parentName="ul">{`Scope: Global`}</li>
          <li parentName="ul">{`Secret: 'your-sauce-username'`}</li>
          <li parentName="ul">{`ID: 'sauce-username'`}</li>
          <li parentName="ul">{`Description: Sauce Labs Username`}</li>
        </ul>
      </li>
      <li parentName="ul">
        <p parentName="li">{`Repeat the above steps for your Sauce Labs Access Key`}</p>
  <img src={useBaseUrl('img/stt/secrets.png')} alt="Jenkins Secrets" width="500" />
        <blockquote parentName="li">
          <p parentName="blockquote">{`For further information on how to store your Sauce Labs credentials in Jenkins, visit `}<a parentName="p" {...{
              "href": "https://www.jenkins.io/doc/book/pipeline/jenkinsfile/#handling-credentials"
            }}>{`the Jenkinsfile documentation`}</a>{`.`}</p>
        </blockquote>
      </li>
    </ul>
    <h3 {...{
      "id": "configure-the-jenkins-pipeline"
    }}>{`Configure the Jenkins Pipeline`}</h3>
    <p>{`Add the `}<inlineCode parentName="p">{`Jenkinsfile`}</inlineCode>{` at the root of your project directory so that Jenkins can detect changes and run `}<inlineCode parentName="p">{`saucectl`}</inlineCode>{` accordingly.
In the examples below, the `}<inlineCode parentName="p">{`environment`}</inlineCode>{` variables are the GitHub secrets configured in Jenkins:`}</p>
    <Tabs defaultValue="cypress" values={[{
      label: 'Cypress',
      value: 'cypress'
    }, {
      label: 'Playwright',
      value: 'playwright'
    }, {
      label: 'TestCafe',
      value: 'testcafe'
    }]} mdxType="Tabs">
      <TabItem value="cypress" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-bash",
            "metastring": "reference",
            "reference": true
          }}>{`https://github.com/saucelabs/testrunner-toolkit/blob/master/.jenkins/Jenkinsfile.cypress
`}</code></pre>
      </TabItem>
      <TabItem value="playwright" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-sh",
            "metastring": "reference",
            "reference": true
          }}>{`https://github.com/saucelabs/testrunner-toolkit/blob/master/.jenkins/Jenkinsfile.playwright
`}</code></pre>
      </TabItem>
      <TabItem value="testcafe" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-bash",
            "metastring": "reference",
            "reference": true
          }}>{`https://github.com/saucelabs/testrunner-toolkit/blob/master/.jenkins/Jenkinsfile.testcafe
`}</code></pre>
      </TabItem>
    </Tabs>
    <blockquote>
      <p parentName="blockquote">{`You can view some of our public examples `}<a parentName="p" {...{
          "href": "https://github.com/saucelabs/testrunner-toolkit/blob/master/.jenkins/"
        }}>{`here`}</a>{`.`}</p>
    </blockquote>
    {
      /*### Run the Pipeline Tests*/
    }
    <p>{`Now you can commit these files and Jenkins will detect the new pipeline and launch `}<inlineCode parentName="p">{`saucetl`}</inlineCode>{` to run your tests.`}</p>
    <p>{`For example if you're using the `}<a parentName="p" {...{
        "href": "https://plugins.jenkins.io/blueocean/"
      }}>{`Blue Ocean plugin`}</a>{`, your output may look something like this:`}</p>
    <img src={useBaseUrl('img/stt/blue-ocean.png')} alt="GitHub Settings" />
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
