  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'configuration',
	title: 'Testrunner Configuration',
	sidebar_label: 'Configuration'
};
export const metadata = {
  "unversionedId": "products/testrunner-toolkit/configuration",
  "id": "products/testrunner-toolkit/configuration",
  "isDocsHomePage": false,
  "title": "Testrunner Configuration",
  "description": "The Testrunner Toolkit requires a configuration file to know which tests to run, along with which framework to use.",
  "source": "@site/docs/products/testrunner-toolkit/configuration.md",
  "permalink": "/products/testrunner-toolkit/configuration",
  "editUrl": "https://github.com/saucelabs/sauce-docs/edit/master/docs/products/testrunner-toolkit/configuration.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar_label": "Configuration",
  "sidebar": "docs",
  "previous": {
    "title": "Testrunner Test Preparation",
    "permalink": "/products/testrunner-toolkit/preparation"
  },
  "next": {
    "title": "Testrunner CI Integrations",
    "permalink": "/products/testrunner-toolkit/integrations"
  }
};
  /* @jsx mdx */
import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
export const rightToc = [{
  value: 'Basic Configuration',
  id: 'basic-configuration',
  children: []
}, {
  value: 'Configuration Examples',
  id: 'configuration-examples',
  children: []
}, {
  value: 'Prepare your environment',
  id: 'prepare-your-environment',
  children: []
}, {
  value: 'Parallelization',
  id: 'parallelization',
  children: []
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`The Testrunner Toolkit requires a configuration file to know which tests to run, along with which framework to use.`}</p>
    <h2 {...{
      "id": "basic-configuration"
    }}>{`Basic Configuration`}</h2>
    <p>{`By default, `}<inlineCode parentName="p">{`config.yml`}</inlineCode>{` is the file `}<inlineCode parentName="p">{`saucectl`}</inlineCode>{` looks to for its configuration.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`#Simple config.yml using puppeteer
apiVersion: v1
metadata:
  name: Feature XYZ
  tags:
    - e2e
    - release team
    - other tag
  build: Release $CI_COMMIT_SHORT_SHA
files:
  - ./tests/**/*.js
image:
  base: saucelabs/sauce-puppeteer-jest-node
  version: v0.1.0
sauce:
  region: us-west-1
`}</code></pre>
    <p>{`If you wish to use more than one framework, or to configure different sets of tests separately, you can use any name for the configuration file and specify it with the following command:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`saucectl run -c ./path/to/config.yml
`}</code></pre>
    <h2 {...{
      "id": "configuration-examples"
    }}>{`Configuration Examples`}</h2>
    <p>{`Below are framework-specific configuration examples that exist in the `}<a parentName="p" {...{
        "href": "https://github.com/saucelabs/testrunner-toolkit/tree/master/.sauce"
      }}>{`Testrunner Toolkit repository`}</a>{`. The repository uses these configurations for its pipeline:`}</p>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`NOTE:`}</strong>{` The test files need to match `}<inlineCode parentName="p">{`(spec|test)`}</inlineCode>{` in their file name so they are automatically detected as `}<inlineCode parentName="p">{`testfiles`}</inlineCode>{`.`}</p>
    </blockquote>
    <Tabs defaultValue="puppeteer" values={[{
      label: 'Puppeteer',
      value: 'puppeteer'
    }, {
      label: 'Playwright',
      value: 'playwright'
    }, {
      label: 'TestCafe',
      value: 'testcafe'
    }, {
      label: 'Cypress',
      value: 'cypress'
    }]} mdxType="Tabs">
      <TabItem value="puppeteer" mdxType="TabItem">
        <p><a parentName="p" {...{
            "href": "https://github.com/saucelabs/testrunner-toolkit/blob/master/.sauce/puppeteer.yml"
          }}><strong parentName="a"><inlineCode parentName="strong">{`puppeteer.yml`}</inlineCode></strong></a></p>
        <pre><code parentName="pre" {...{
            "className": "language-yaml"
          }}>{`apiVersion: v1
# type of object, there could be theoretically other types
# of "orchestration" in the future, e.g. network config maps etc.
kind: Test
# meta data to the test
metadata:
  name: Feature XYZ
  tags:
    - e2e
    - release team
    - other tag
  build: "Build #$BUILD_ID in $BUILD_ENV"
# Every file defined in this list will be bundled into a zip and
# uploaded to Sauce Labs.
files:
  - ./tests/puppeteer/demo.test.js
  # - ./tests/puppeteer/sauce-swag-checkout.test.js
  # - ./tests/puppeteer/sauce-swag-login.test.js
# Define a test runner image (e.g. an image to run WebdriverIO tests)
# Like in Docker, these images can be developed as Open Source projects
# and maintained by our teams, while at the same time, customers can
# build their own images as well
image:
  # while a set of properties are defined by our Yaml format
  base: saucelabs/sauce-puppeteer
  version: 3.0.4-saucectl0.6.2
`}</code></pre>
      </TabItem>
      <TabItem value="playwright" mdxType="TabItem">
        <p><a parentName="p" {...{
            "href": "https://github.com/saucelabs/testrunner-toolkit/blob/master/.sauce/playwright.yml"
          }}><strong parentName="a"><inlineCode parentName="strong">{`playwright.yml`}</inlineCode></strong></a></p>
        <pre><code parentName="pre" {...{
            "className": "language-yaml"
          }}>{`apiVersion: v1
# type of object, there could be theoretically other types
# of "orchestration" in the future, e.g. network config maps etc.
kind: Test
# meta data to the test
metadata:
  name: Feature XYZ
  tags:
    - e2e
    - release team
    - other tag
  build: "Build #$BUILD_ID in $BUILD_ENV"
# Every file defined in this list will be bundled into a zip and
# uploaded to Sauce Labs.
files:
  - ./tests/playwright/demo.test.js
# Define a test runner image (e.g. an image to run WebdriverIO tests)
# Like in Docker, these images can be developed as Open Source projects
# and maintained by our teams, while at the same time, customers can
# build their own images as well
image:
  # while a set of properties are defined by our Yaml format
  base: saucelabs/sauce-playwright
  version: 1.0.0-saucectl0.6.2
`}</code></pre>
      </TabItem>
      <TabItem value="testcafe" mdxType="TabItem">
        <p><a parentName="p" {...{
            "href": "https://github.com/saucelabs/testrunner-toolkit/blob/master/.sauce/testcafe.yml"
          }}><strong parentName="a"><inlineCode parentName="strong">{`testcafe.yml`}</inlineCode></strong></a></p>
        <pre><code parentName="pre" {...{
            "className": "language-yaml"
          }}>{`apiVersion: v1
metadata:
  name: Feature XYZ
  tags:
    - e2e
    - release team
    - other tag
  build: Release $CI_COMMIT_SHORT_SHA
files:
  - ./tests/testcafe/*.js
capabilities:
  - browserName: Chrome
image:
  base: saucelabs/sauce-testcafe
  version: 1.8.5-saucectl0.6.3
`}</code></pre>
      </TabItem>
      <TabItem value="cypress" mdxType="TabItem">
        <p><a parentName="p" {...{
            "href": "https://github.com/saucelabs/testrunner-toolkit/blob/master/.sauce/cypress.yml"
          }}><strong parentName="a"><inlineCode parentName="strong">{`cypress.yml`}</inlineCode></strong></a></p>
        <pre><code parentName="pre" {...{
            "className": "language-yaml"
          }}>{`apiVersion: v1
metadata:
  name: Feature XYZ
  tags:
    - e2e
    - release team
    - other tag
files:
  - ./tests/cypress/example.test.js
image:
  base: saucelabs/stt-cypress-mocha-node
  version: v0.1.3
`}</code></pre>
      </TabItem>
    </Tabs>
    <h2 {...{
      "id": "prepare-your-environment"
    }}>{`Prepare your environment`}</h2>
    <p>{`Saucectl offers the possibility to setup your tests environment before executing any of your suites using `}<inlineCode parentName="p">{`beforeExec`}</inlineCode>{`: `}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`beforeExec:
  - npm install --save chai
`}</code></pre>
    <h2 {...{
      "id": "parallelization"
    }}>{`Parallelization`}</h2>
    <p>{`Saucectl is capable of running tests in parallel by utilizing multiple CI machines. `}<em parentName="p">{`This feature requires a Sauce Labs account`}</em>{`, so don't forget to set the environment variables `}<inlineCode parentName="p">{`SAUCE_USERNAME`}</inlineCode>{` and `}<inlineCode parentName="p">{`SAUCE_ACCESS_KEY`}</inlineCode>{`!`}</p>
    <p>{`Parallelization can be turned on either via the config`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`parallel: true
`}</code></pre>
    <p>{`or the CLI`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`saucectl run --parallel
`}</code></pre>
    <p>{`The concrete setup of the pipeline will depend on your CI provider however. `}<a parentName="p" {...{
        "href": "https://github.com/saucelabs/saucectl/blob/master/.github/workflows/test.yml#L94-L145"
      }}>{`Here's an example`}</a>{` how to set it up for GitHub Actions.`}</p>
    <p>{`Please visit `}<a parentName="p" {...{
        "href": "/products/testrunner-toolkit/saucectl#parallel"
      }}>{`here`}</a>{` for more information about the parallelization feature and its limitations.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
