  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'github-actions',
	title: 'Testrunner Toolkit with GitHub Actions',
	sidebar_label: 'GitHub Actions'
};
export const metadata = {
  "unversionedId": "testrunner-toolkit/integrations/github-actions",
  "id": "testrunner-toolkit/integrations/github-actions",
  "isDocsHomePage": false,
  "title": "Testrunner Toolkit with GitHub Actions",
  "description": "These examples can apply to virtually any GitHub deployment, provided that you already have some existing automated tests, and are either the maintainer or an admin of the target repository.",
  "source": "@site/docs/testrunner-toolkit/integrations/github-actions.md",
  "slug": "/testrunner-toolkit/integrations/github-actions",
  "permalink": "/testrunner-toolkit/integrations/github-actions",
  "editUrl": "https://github.com/saucelabs/sauce-docs/edit/master/docs/testrunner-toolkit/integrations/github-actions.md",
  "version": "current",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar_label": "GitHub Actions",
  "sidebar": "someSidebar",
  "previous": {
    "title": "Testrunner Toolkit with Jenkins",
    "permalink": "/testrunner-toolkit/integrations/jenkins"
  },
  "next": {
    "title": "Using TypeScript Tests",
    "permalink": "/testrunner-toolkit/typescript"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */
import useBaseUrl from '@docusaurus/useBaseUrl';
import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
export const toc = [{
  value: 'What You&#39;ll Need',
  id: 'what-youll-need',
  children: []
}, {
  value: 'Create GitHub Secrets',
  id: 'create-github-secrets',
  children: []
}, {
  value: 'Configure the GitHub Action',
  id: 'configure-the-github-action',
  children: []
}, {
  value: 'Create the Test Job',
  id: 'create-the-test-job',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`These examples can apply to virtually any GitHub deployment, provided that you already have some existing automated tests, and are either the maintainer or an admin of the target repository.`}</p>
    <h2 {...{
      "id": "what-youll-need"
    }}>{`What You'll Need`}</h2>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://github.com/join"
        }}>{`GitHub Account`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://saucelabs.com/sign-up"
        }}>{`Sauce Labs Account`}</a></li>
      <li parentName="ul">{`The following permissions in GitHub:`}
        <ul parentName="li">
          <li parentName="ul">{`ability to create and manage workflows`}</li>
          <li parentName="ul">{`ability to create and store `}<a parentName="li" {...{
              "href": "https://docs.github.com/en/free-pro-team@latest/actions/reference/encrypted-secrets"
            }}>{`GitHub secrets`}</a></li>
        </ul>
      </li>
    </ul>
    <h2 {...{
      "id": "create-github-secrets"
    }}>{`Create GitHub Secrets`}</h2>
    <p>{`The first order of business is to export your `}<a parentName="p" {...{
        "href": "https://app.saucelabs.com/user-settings"
      }}>{`Sauce Labs account credentials`}</a>{` and store them as GitHub Secrets.`}</p>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`Navigate to your project repository and select the `}<strong parentName="p">{`settings`}</strong>{` icon`}</p>
        <img src={useBaseUrl('img/stt/github-settings.png')} alt="GitHub Settings" width="500" />
      </li>
      <li parentName="ol">
        <p parentName="li">{`Select `}<strong parentName="p">{`Secrets`}</strong></p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Click the `}<strong parentName="p">{`New secret`}</strong>{` button`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Add the following:`}</p>
        <ul parentName="li">
          <li parentName="ul">{`Name: `}<inlineCode parentName="li">{`SAUCE_USERNAME`}</inlineCode></li>
          <li parentName="ul">{`Value: 'your-sauce-username'`}</li>
        </ul>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Click `}<strong parentName="p">{`Add secret`}</strong>{` to finish.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Repeat the same steps above for your `}<inlineCode parentName="p">{`SAUCE_ACCESS_KEY`}</inlineCode>{` (Not sure where to find `}<inlineCode parentName="p">{`SAUCE_USERNAME`}</inlineCode>{` and `}<inlineCode parentName="p">{`SAUCE_ACCESS_KEY`}</inlineCode>{` in Sauce Labs? They're `}<a parentName="p" {...{
            "href": "https://app.saucelabs.com/user-settings"
          }}>{`here`}</a>{`).`}</p>
      </li>
    </ol>
    <h2 {...{
      "id": "configure-the-github-action"
    }}>{`Configure the GitHub Action`}</h2>
    <p>{`In your root project directory, create the following directory tree: `}<inlineCode parentName="p">{`.github/workflows`}</inlineCode>{`. In the `}<inlineCode parentName="p">{`workflows`}</inlineCode>{` directory create a file called `}<inlineCode parentName="p">{`actions.yml`}</inlineCode>{`. `}</p>
    <p>{`Add the following to the top of your file:`}</p>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`NOTE`}</strong>{`: Setting `}<inlineCode parentName="p">{`env`}</inlineCode>{` at the top of the file enables it globally in this workflow, so all jobs have access to these variables.`}</p>
    </blockquote>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`name: Sauce Pipeline Browser Tests

on:
  pull_request:
  push:
    branches:
      - master

env:
  SAUCE_ACCESS_KEY: \${{secrets.SAUCE_ACCESS_KEY}}
  SAUCE_USERNAME: \${{secrets.SAUCE_USERNAME}}

jobs:
`}</code></pre>
    <h2 {...{
      "id": "create-the-test-job"
    }}>{`Create the Test Job`}</h2>
    <p>{`In the examples below, the environment variables (`}<inlineCode parentName="p">{`env`}</inlineCode>{`) equate to the values configured in GitHub secrets (see above steps). These events only trigger test runs `}<inlineCode parentName="p">{`on`}</inlineCode>{` every `}<inlineCode parentName="p">{`pull_request`}</inlineCode>{` and/or `}<inlineCode parentName="p">{`push`}</inlineCode>{` to the `}<inlineCode parentName="p">{`master`}</inlineCode>{` branch.`}</p>
    <blockquote>
      <p parentName="blockquote">{`For more detailed information on setting event-driven actions and jobs, please visit the `}<a parentName="p" {...{
          "href": "https://docs.github.com/en/free-pro-team@latest/actions/learn-github-actions/introduction-to-github-actions#the-components-of-github-actions"
        }}>{`GitHub Action documentation`}</a>{`.`}</p>
    </blockquote>
    <Tabs defaultValue="cypress" values={[{
      label: 'Cypress',
      value: 'cypress'
    }, {
      label: 'Playwright',
      value: 'playwright'
    }, {
      label: 'TestCafe',
      value: 'testcafe'
    }]} mdxType="Tabs">

      <TabItem value="cypress" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-yaml",
            "metastring": "reference",
            "reference": true
          }}>{`https://github.com/saucelabs/testrunner-toolkit/blob/master/.github/workflows/tests.yml#L93-L114
`}</code></pre>
      </TabItem>
      <TabItem value="playwright" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-yaml",
            "metastring": "reference",
            "reference": true
          }}>{`https://github.com/saucelabs/testrunner-toolkit/blob/master/.github/workflows/tests.yml#L44-L67
`}</code></pre>
      </TabItem>
      <TabItem value="testcafe" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-yaml",
            "metastring": "reference",
            "reference": true
          }}>{`https://github.com/saucelabs/testrunner-toolkit/blob/master/.github/workflows/tests.yml#L69-L91
`}</code></pre>
      </TabItem>
    </Tabs>
    <blockquote>
      <p parentName="blockquote">{`You can reference our example workflows `}<a parentName="p" {...{
          "href": "https://github.com/saucelabs/testrunner-toolkit/tree/master/.github/workflows"
        }}>{`here`}</a>{`.`}</p>
    </blockquote>
    <p>{`Now when you commit these files, GitHub will detect the new workflow actions and launch `}<inlineCode parentName="p">{`saucectl`}</inlineCode>{` to run your tests.`}</p>
    <p>{`To see the output:`}</p>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`Log in to GitHub`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Navigate to your repository page`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Click on Actions`}</p>
        <img src={useBaseUrl('img/stt/github-actions.png')} alt="GitHub Actions" width="500" />
      </li>
    </ol>
    <p>{`Your output may look something like this:`}</p>
    <img src={useBaseUrl('img/stt/github-workflow.png')} alt="GitHub Workflow" width="800" />
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
