  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'getting-started',
	title: 'Getting Started with Sauce Labs',
	sidebar_label: 'Getting Started'
};
export const metadata = {
  "unversionedId": "getting-started",
  "id": "getting-started",
  "isDocsHomePage": false,
  "title": "Getting Started with Sauce Labs",
  "description": "Welcome to Sauce Labs! Your path for getting started depends on your experience level and use case. Below are the various test scenarios that Sauce Labs supports.",
  "source": "@site/docs/getting-started.md",
  "permalink": "/getting-started",
  "editUrl": "https://github.com/saucelabs/sauce-docs/edit/master/docs/getting-started.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar_label": "Getting Started",
  "sidebar": "docs",
  "previous": {
    "title": "Documentation",
    "permalink": "/overview"
  },
  "next": {
    "title": "Application Storage",
    "permalink": "/storage"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Live Tests',
  id: 'live-tests',
  children: []
}, {
  value: 'Automated Tests',
  id: 'automated-tests',
  children: [{
    value: 'Supported Frameworks',
    id: 'supported-frameworks',
    children: []
  }]
}, {
  value: 'Run Your First Test',
  id: 'run-your-first-test',
  children: []
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Welcome to Sauce Labs! Your path for getting started depends on your experience level and use case. Below are the various test scenarios that Sauce Labs supports.`}</p>
    <h2 {...{
      "id": "live-tests"
    }}>{`Live Tests`}</h2>
    <p>{`The topics below contain information about live testing of Web Apps on desktop browser, and native browsers for real and virtual devices, with Sauce Labs. Topics include:`}</p>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "/guides/live-testing/web-apps#testing-on-desktop-browsers"
        }}>{`Live Web App Testing on Desktop Browsers`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "/guides/live-testing/web-apps#testing-on-mobile-browsers"
        }}>{`Live Web App Testing on Mobile Browsers`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "/guides/live-testing/mobile-apps#virtual-device-testing"
        }}>{`Live Mobile App Testing on Virtual Devices`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "/guides/live-testing/mobile-apps#real-device-testing"
        }}>{`Live Mobile App Testing on Real Devices`}</a></li>
    </ul>
    <h2 {...{
      "id": "automated-tests"
    }}>{`Automated Tests`}</h2>
    <p>{`If you're starting from scratch, or haven't committed to a specific test framework, check out the `}<a parentName="p" {...{
        "href": "https://github.com/saucelabs-training"
      }}>{`Sauce Labs Training Organization`}</a>{` for demos that show you how to:`}</p>
    <ul>
      <li parentName="ul">{`configure your test environment`}</li>
      <li parentName="ul">{`configure your tests to communicate with our VMs and send results to Sauce Labs.`}</li>
    </ul>
    <p>{`Please visit the `}<a parentName="p" {...{
        "href": "/guides/automation/getting-started"
      }}>{`Automated Testing`}</a>{` section of the docs to get started.`}</p>
    <h3 {...{
      "id": "supported-frameworks"
    }}>{`Supported Frameworks`}</h3>
    <p>{`Below are a few of the frameworks that Sauce Labs support:`}</p>
    <ul>
      <li parentName="ul">
        <p parentName="li"><strong parentName="p"><a parentName="strong" {...{
              "href": "https://www.selenium.dev/"
            }}>{`Selenium`}</a>{` and `}<a parentName="strong" {...{
              "href": "http://appium.io/docs/"
            }}>{`Appium`}</a></strong>{` - For more detailed information, please visit the `}<a parentName="p" {...{
            "href": "/guides/automation/getting-started#selenium-or-appium-tests"
          }}>{`Getting Started with Selenium or Appium Tests`}</a>{` page.`}</p>
      </li>
      <li parentName="ul">
        <p parentName="li"><strong parentName="p"><a parentName="strong" {...{
              "href": "https://webdriver.io/"
            }}>{`WebDriverIO`}</a></strong>{` - is a next-gen browser and mobile automation test framework for Node.js. For more information, view some of our `}<a parentName="p" {...{
            "href": "https://github.com/saucelabs-training/demo-js"
          }}>{`code examples`}</a>{` to learn how to use `}<inlineCode parentName="p">{`wdio`}</inlineCode>{` with the Sauce Labs platform.`}</p>
      </li>
      <li parentName="ul">
        <p parentName="li"><strong parentName="p"><a parentName="strong" {...{
              "href": "https://developer.apple.com/documentation/xctest/user_interface_tests"
            }}>{`XCUITest`}</a>{` and `}<a parentName="strong" {...{
              "href": "https://developer.android.com/training/testing/espresso"
            }}>{`Espresso`}</a></strong>{` - Sauce Labs allows you to test your mobile application with native mobile test frameworks using the `}<a parentName="p" {...{
            "href": ""
          }}>{`Sauce Runner`}</a>{`.`}</p>
      </li>
      <li parentName="ul">
        <p parentName="li"><strong parentName="p"><a parentName="strong" {...{
              "href": "https://github.com/puppeteer/puppeteer"
            }}>{`Puppeteer`}</a>{`, `}<a parentName="strong" {...{
              "href": "https://github.com/microsoft/playwright"
            }}>{`Playwright`}</a>{`, `}<a parentName="strong" {...{
              "href": "https://github.com/DevExpress/testcafe"
            }}>{`TestCafe`}</a>{`, and `}<a parentName="strong" {...{
              "href": "https://github.com/cypress-io/cypress"
            }}>{`Cypress.io`}</a></strong>{` - Refer to our `}<a parentName="p" {...{
            "href": "/products/testrunner-toolkit"
          }}>{`Testrunner Toolkit Documentation`}</a>{` for more information on integrating your JavaScript framework tests with the Sauce Labs platform.`}</p>
      </li>
    </ul>
    <blockquote>
      <p parentName="blockquote">{`If you don't know much about test automation or need framework-specific training, check out `}<a parentName="p" {...{
          "href": ""
        }}>{`Sauce School`}</a>{` to get started.`}</p>
    </blockquote>
    <h2 {...{
      "id": "run-your-first-test"
    }}>{`Run Your First Test`}</h2>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
