};
  /* @jsxRuntime classic */
/* @jsx mdx */
import useBaseUrl from '@docusaurus/useBaseUrl';
import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
export const toc = [{
  value: 'OS X 10.6, 10.8, 10.9, 10.10',
  id: 'os-x-106-108-109-1010',
  children: []
}, {
  value: 'Windows 7, 8, 8.1',
  id: 'windows-7-8-81',
  children: []
}, {
  value: 'Windows XP',
  id: 'windows-xp',
  children: []
}, {
  value: 'Linux',
  id: 'linux',
  children: []
}, {
  value: 'Set Prerun Capability',
  id: 'set-prerun-capability',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`You can use a pre-run executable script to download files from a public location to the Sauce Labs virtual machine running your tests. This topic contains example scripts for downloading remote files on different operating systems, and details configuring the prerun capability in your tests.`}</p>
    <Tabs defaultValue="macos" values={[{
      label: 'macOS',
      value: 'macos'
    }, {
      label: 'Windows 7, 8, 8.1',
      value: 'windows'
    }, {
      label: 'Windows XP',
      value: 'windowsxp'
    }, {
      label: 'Linux',
      value: 'linux'
    }]} mdxType="Tabs">
      <TabItem value="macos" mdxType="TabItem">
        <h3 {...{
          "id": "os-x-106-108-109-1010"
        }}>{`OS X 10.6, 10.8, 10.9, 10.10`}</h3>
        <p>{`This shell script will fetch the file at the URL and save it to `}<strong parentName="p">{`/tmp/file.txt`}</strong>{`.`}</p>
        <pre><code parentName="pre" {...{
            "className": "language-js"
          }}>{`#!/bin/bash
curl -o /tmp/file.txt http://mywebsite.com/file.txt
`}</code></pre>
      </TabItem>
      <TabItem value="windows" mdxType="TabItem">
        <h3 {...{
          "id": "windows-7-8-81"
        }}>{`Windows 7, 8, 8.1`}</h3>
        <p>{`This batch file accomplishes the same thing as the OS X curl method, but using `}<strong parentName="p">{`bitsadmin.exe`}</strong>{` since Windows doesn't ship with curl.`}</p>
        <pre><code parentName="pre" {...{
            "className": "language-js"
          }}>{`@echo off
bitsadmin.exe /transfer "JobName" http://mywebsite.com/file.txt C:\\Users\\Administrator\\Desktop\\file.txt
`}</code></pre>
      </TabItem>
      <TabItem value="windowsxp" mdxType="TabItem">
        <h3 {...{
          "id": "windows-xp"
        }}>{`Windows XP`}</h3>
        <p>{`This batch file creates a VBScript file, `}<strong parentName="p">{`dl.vbs`}</strong>{`, which will perform the download, and then runs it:`}</p>
        <pre><code parentName="pre" {...{
            "className": "language-js"
          }}>{`@echo off

echo strFileURL="http://mywebsite.com/file.txt" > C:\\dl.vbs
echo strHDLocation = "C:\\file.csv" >> C:\\dl.vbs
echo Set objXMLHTTP = CreateObject("MSXML2.XMLHTTP") >> C:\\dl.vbs
echo objXMLHTTP.open "GET", strFileURL, false >> C:\\dl.vbs
echo objXMLHTTP.send() >> C:\\dl.vbs
echo If objXMLHTTP.Status = 200 Then >> C:\\dl.vbs
echo Set objADOStream = CreateObject("ADODB.Stream") >> C:\\dl.vbs
echo objADOStream.Open >> C:\\dl.vbs
echo objADOStream.Type = 1 'adTypeBinary >> C:\\dl.vbs
echo objADOStream.Write objXMLHTTP.ResponseBody >> C:\\dl.vbs
echo objADOStream.Position = 0 'Set the stream position to the start >> C:\\dl.vbs
echo Set objFSO = Createobject("Scripting.FileSystemObject") >> C:\\dl.vbs
echo If objFSO.Fileexists(strHDLocation) Then objFSO.DeleteFile strHDLocation >> C:\\dl.vbs
echo Set objFSO = Nothing >> C:\\dl.vbs
echo objADOStream.SaveToFile strHDLocation >> C:\\dl.vbs
echo objADOStream.Close >> C:\\dl.vbs
echo Set objADOStream = Nothing >> C:\\dl.vbs
echo End if >> C:\\dl.vbs
echo Set objXMLHTTP = Nothing >> C:\\dl.vbs

cscript.exe C:\\dl.vbs
`}</code></pre>
      </TabItem>
      <TabItem value="linux" mdxType="TabItem">
        <h3 {...{
          "id": "linux"
        }}>{`Linux`}</h3>
        <p>{`This shell script downloads `}<strong parentName="p">{`file.txt`}</strong>{` at `}<strong parentName="p">{`mywebsite.com`}</strong>{` to the `}<strong parentName="p">{`/tmp`}</strong>{` directory.`}</p>
        <pre><code parentName="pre" {...{
            "className": "language-js"
          }}>{`#!/bin/bash
wget -O /tmp/file.txt http://mywebsite.com/file.txt
`}</code></pre>
      </TabItem>
    </Tabs>
    <h2 {...{
      "id": "set-prerun-capability"
    }}>{`Set Prerun Capability`}</h2>
    <p>{`After you've created the download script, use the prerun capability in your test script to point to its location.`}</p>
    <p>{`If your script is in a publicly accessible location, you need to add the URL to the prerun capability.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-js"
      }}>{`capabilities['prerun'] = "http://location.of/curl.sh"
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
