/* @jsx mdx */
import useBaseUrl from '@docusaurus/useBaseUrl';
export const toc = [{
  value: 'Virtual Device Cloud Billing',
  id: 'virtual-device-cloud-billing',
  children: [{
    value: 'Payment Tab',
    id: 'payment-tab',
    children: []
  }, {
    value: 'Billing Information Tab',
    id: 'billing-information-tab',
    children: []
  }]
}, {
  value: 'Real Device Cloud Billing',
  id: 'real-device-cloud-billing',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`You can update your plan billing information at any time for our online plans.`}</p>
    <p><strong parentName="p">{`NOTE:`}</strong>{` If you want to update the billing information for an Enterprise plan, contact your Sauce Labs account executive.`}</p>
    <p><strong parentName="p">{`NOTE:`}</strong>{` Sauce Labs is currently migrating its billing system, so the steps to update your billing information will depend on what tab is available.`}</p>
    <h2 {...{
      "id": "virtual-device-cloud-billing"
    }}>{`Virtual Device Cloud Billing`}</h2>
    <p>{`In Sauce Labs, click `}<strong parentName="p">{`Account`}</strong>{`, and then click `}<strong parentName="p">{`Billing`}</strong>{`.`}</p>
    <img src={useBaseUrl('img/team-mgmt/billing-nav.jpg')} alt="Billing navigation" width="400" />
    <h3 {...{
      "id": "payment-tab"
    }}>{`Payment Tab`}</h3>
    <p>{`If the `}<strong parentName="p">{`Payment`}</strong>{` tab is visible:`}</p>
    <ol>
      <li parentName="ol">{`Click the `}<strong parentName="li">{`Payment`}</strong>{` tab and then click `}<strong parentName="li">{`Change Credit Card`}</strong>{`.`}</li>
      <li parentName="ol">{`Update your billing information.`}</li>
      <li parentName="ol">{`Select the checkbox to acknowledge you are replacing the card on file.`}</li>
      <li parentName="ol">{`Click `}<strong parentName="li">{`Save Payment Method`}</strong>{`.`}</li>
    </ol>
    <p><strong parentName="p">{`NOTE:`}</strong>{` For your reference, the URL for this page is `}<strong parentName="p">{`billing.saucelabs.com`}</strong>{`.`}</p>
    <h3 {...{
      "id": "billing-information-tab"
    }}>{`Billing Information Tab`}</h3>
    <p>{`If the `}<strong parentName="p">{`Billing Information`}</strong>{` tab is visible:`}</p>
    <ol>
      <li parentName="ol">{`Click the `}<strong parentName="li">{`Billing Information`}</strong>{` tab.`}</li>
      <li parentName="ol">{`Update cardholder details (if required).`}</li>
      <li parentName="ol">{`To update the card on file, click `}<strong parentName="li">{`Replace Existing Card`}</strong>{`.`}</li>
      <li parentName="ol">{`Click `}<strong parentName="li">{`Update Payment`}</strong>{`.`}</li>
    </ol>
    <p><strong parentName="p">{`NOTE:`}</strong>{` For your reference, the URL for this page is `}<strong parentName="p">{`https:`}<span></span>{`//app.saucelabs.com/billing/my-account`}</strong>{`.`}</p>
    <h2 {...{
      "id": "real-device-cloud-billing"
    }}>{`Real Device Cloud Billing`}</h2>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`In Sauce Labs, in the left panel, click `}<strong parentName="p">{`Sauce Apps`}</strong>{` and then click `}<strong parentName="p">{`Legacy RDC`}</strong>{`.`}</p>
        <img src={useBaseUrl('img/team-mgmt/legacy-rdc-nav.jpg')} alt="Legacy RDC navigation" width="400" />
      </li>
      <li parentName="ol">
        <p parentName="li">{`Click the person icon, and then click `}<strong parentName="p">{`Account Settings`}</strong>{`.`}</p>
        <img src={useBaseUrl('img/team-mgmt/legacy-rdc-acct-settings.jpg')} alt="Legacy RDC account settings" width="400" />
      </li>
      <li parentName="ol">
        <p parentName="li">{`On the `}<strong parentName="p">{`Billing`}</strong>{` tab, click `}<strong parentName="p">{`Payment Data`}</strong>{`.`}</p>
        <img src={useBaseUrl('img/team-mgmt/rdc-billing-payment-data.jpg')} alt="Legacy RDC billing payment data" width="500" />
      </li>
      <li parentName="ol">
        <p parentName="li">{`In the `}<strong parentName="p">{`Update Payment Data`}</strong>{` window, update the billing information and/or credit card on file, and then click `}<strong parentName="p">{`Save`}</strong>{`.`}</p>
      </li>
    </ol>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
