import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
export const toc = [{
  value: 'What You&#39;ll Need',
  id: 'what-youll-need',
  children: [{
    value: 'System Requirements',
    id: 'system-requirements',
    children: []
  }]
}, {
  value: 'Installing Testrunner Toolkit',
  id: 'installing-testrunner-toolkit',
  children: []
}, {
  value: 'Connecting to Sauce Labs',
  id: 'connecting-to-sauce-labs',
  children: [{
    value: 'Using Environment Variables',
    id: 'using-environment-variables',
    children: []
  }, {
    value: 'Using the Configure Command',
    id: 'using-the-configure-command',
    children: []
  }]
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`Sauce Labs Testrunner Toolkit is a containerized testing solution that simplifies user setup, speeds up test execution time and supports native Javascript frameworks like, `}<a parentName="p" {...{
        "href": "https://github.com/cypress-io/cypress"
      }}>{`Cypress`}</a>{` and `}<a parentName="p" {...{
        "href": "https://devexpress.github.io/testcafe/"
      }}>{`TestCafe`}</a>{`, for running end-to-end web tests with `}<a parentName="p" {...{
        "href": "https://saucelabs.com/"
      }}>{`Sauce Labs`}</a>{`.`}</p>
    <h2 {...{
      "id": "what-youll-need"
    }}>{`What You'll Need`}</h2>
    <ul>
      <li parentName="ul">{`A `}<a parentName="li" {...{
          "href": "https://saucelabs.com/"
        }}>{`Sauce Labs`}</a>{` account (if you don't have one, start a `}<a parentName="li" {...{
          "href": "https://saucelabs.com/sign-up"
        }}>{`free trial`}</a>{`)`}</li>
      <li parentName="ul">{`Install `}<a parentName="li" {...{
          "href": "https://docs.docker.com/get-docker/"
        }}>{`Docker`}</a>
        <blockquote parentName="li">
          <p parentName="blockquote">{`Ensure the `}<a parentName="p" {...{
              "href": "https://docs.docker.com/config/daemon/"
            }}>{`Docker daemon`}</a>{` is running (e.g. `}<inlineCode parentName="p">{`docker info`}</inlineCode>{` works in your terminal / command prompt`}</p>
        </blockquote>
      </li>
      <li parentName="ul">{`Know which `}<a parentName="li" {...{
          "href": "/testrunner-toolkit#supported-frameworks-and-browsers"
        }}>{`test framework and browser versions`}</a>{` you plan to run tests against`}</li>
    </ul>
    <h3 {...{
      "id": "system-requirements"
    }}>{`System Requirements`}</h3>
    <p>{`The system requirements to successfully run `}<inlineCode parentName="p">{`saucectl`}</inlineCode>{` vary depending on whether you plan to run it locally via `}<a parentName="p" {...{
        "href": "/testrunner-toolkit/running-tests#run-your-first-test"
      }}><inlineCode parentName="a">{`docker`}</inlineCode></a>{`, or remotely via `}<a parentName="p" {...{
        "href": "/testrunner-toolkit/running-tests#test-on-sauce-labs"
      }}><inlineCode parentName="a">{`sauce`}</inlineCode></a>{`. `}</p>
    <p>{`As a rule of thumb, your local system requirements should match the `}<a parentName="p" {...{
        "href": "https://docs.docker.com/engine/install/#supported-platforms"
      }}>{`Docker installation requirements`}</a>{`. Please use the table below as a quick reference:`}</p>
    <Tabs defaultValue="macos" values={[{
      label: 'macOS',
      value: 'macos'
    }, {
      label: 'Linux',
      value: 'linux'
    }, {
      label: 'Windows',
      value: 'windows'
    }]} mdxType="Tabs">

      <TabItem value="macos" mdxType="TabItem">
        <ul>
          <li parentName="ul">{`Docker: `}<a parentName="li" {...{
              "href": "https://docs.docker.com/docker-for-mac/install/"
            }}>{`Desktop`}</a></li>
          <li parentName="ul">{`OS: 10.14+ (Mojave, Catalina, or Big Sur)`}</li>
        </ul>
        <blockquote>
          <p parentName="blockquote">{`Browser support depends on the chosen test framework, see `}<a parentName="p" {...{
              "href": "/testrunner-toolkit#supported-frameworks-and-browsers"
            }}>{`this section`}</a>{` for more details.`}</p>
        </blockquote>
      </TabItem>
      <TabItem value="linux" mdxType="TabItem">
        <ul>
          <li parentName="ul">{`Docker: `}<a parentName="li" {...{
              "href": "https://docs.docker.com/engine/install/#server"
            }}>{`Server`}</a></li>
          <li parentName="ul">{`OS/Distros: `}<a parentName="li" {...{
              "href": "https://docs.docker.com/engine/install/centos/"
            }}>{`CentOS`}</a>{`, `}<a parentName="li" {...{
              "href": "https://docs.docker.com/engine/install/debian/"
            }}>{`Debian`}</a>{`, `}<a parentName="li" {...{
              "href": "https://docs.docker.com/engine/install/fedora/"
            }}>{`Fedora`}</a>{`, `}<a parentName="li" {...{
              "href": "https://docs.docker.com/engine/install/ubuntu/"
            }}>{`Ubuntu`}</a></li>
        </ul>
        <blockquote>
          <p parentName="blockquote">{`Browser support depends on the chosen test framework, see `}<a parentName="p" {...{
              "href": "/testrunner-toolkit#supported-frameworks-and-browsers"
            }}>{`this section`}</a>{` for more details.`}</p>
        </blockquote>
      </TabItem>
      <TabItem value="windows" mdxType="TabItem">
        <ul>
          <li parentName="ul">{`Docker: `}<a parentName="li" {...{
              "href": "https://docs.docker.com/docker-for-windows/install/"
            }}>{`Desktop`}</a></li>
          <li parentName="ul">{`OS: Windows 10 (`}<a parentName="li" {...{
              "href": "https://docs.docker.com/docker-for-windows/install-windows-home/"
            }}>{`Home`}</a>{`, Pro, Enterprise, or Education)`}</li>
        </ul>
        <blockquote>
          <p parentName="blockquote">{`Browser support depends on the chosen test framework, see `}<a parentName="p" {...{
              "href": "/testrunner-toolkit#supported-frameworks-and-browsers"
            }}>{`this section`}</a>{` for more details.`}</p>
        </blockquote>
      </TabItem>
    </Tabs>
    <h2 {...{
      "id": "installing-testrunner-toolkit"
    }}>{`Installing Testrunner Toolkit`}</h2>
    <p>{`There are multiple ways to install the Sauce Labs Testrunner Toolkit (colloquially known as `}<inlineCode parentName="p">{`saucectl`}</inlineCode>{`):`}</p>
    <ul>
      <li parentName="ul">
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash",
            "metastring": "title=\"Using curl\"",
            "title": "\"Using",
            "curl\"": true
          }}>{`curl -L https://saucelabs.github.io/saucectl/install | bash
`}</code></pre>
      </li>
      <li parentName="ul">
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash",
            "metastring": "title=\"Using NPM\"",
            "title": "\"Using",
            "NPM\"": true
          }}>{`npm install -g saucectl
`}</code></pre>
      </li>
      <li parentName="ul">
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash",
            "metastring": "title=\"Using Homebrew (macOS)\"",
            "title": "\"Using",
            "Homebrew": true,
            "(macOS)\"": true
          }}>{`brew tap saucelabs/saucectl
brew install saucectl
`}</code></pre>
      </li>
    </ul>
    <p>{`If you would like to inspect the content of our one line installer, download it, have a look, and execute it:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`curl -fsSL -o get_saucectl.sh https://saucelabs.github.io/saucectl/install && \\
chmod 700 get_saucectl.sh && \\
./get_saucectl.sh
`}</code></pre>
    <h2 {...{
      "id": "connecting-to-sauce-labs"
    }}>{`Connecting to Sauce Labs`}</h2>
    <p>{`You need to retrieve your Sauce Labs `}<inlineCode parentName="p">{`username`}</inlineCode>{` and `}<inlineCode parentName="p">{`accessKey`}</inlineCode>{` in order post your test results to the Sauce Labs platform. To retrieve your Sauce Labs account credentials navigate to `}<a parentName="p" {...{
        "href": "https://app.saucelabs.com/user-settings"
      }}>{`Account > User Settings`}</a>{`.`}</p>
    <h3 {...{
      "id": "using-environment-variables"
    }}>{`Using Environment Variables`}</h3>
    <p>{`Sauce Labs recommends as a best practice to set your account credentials as environment variables like so:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`SAUCE_USERNAME='valid.username'
SAUCE_ACCESS_KEY='valid.key'
`}</code></pre>
    <blockquote>
      <p parentName="blockquote">{`If you are using a cloud CI/CD tool, we strongly suggest protecting these values through secrets or context variables.`}</p>
    </blockquote>
    <div {...{
      "className": "admonition admonition-tip alert alert--success"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.5 0C3.48 0 1 2.19 1 5c0 .92.55 2.25 1 3 1.34 2.25 1.78 2.78 2 4v1h5v-1c.22-1.22.66-1.75 2-4 .45-.75 1-2.08 1-3 0-2.81-2.48-5-5.5-5zm3.64 7.48c-.25.44-.47.8-.67 1.11-.86 1.41-1.25 2.06-1.45 3.23-.02.05-.02.11-.02.17H5c0-.06 0-.13-.02-.17-.2-1.17-.59-1.83-1.45-3.23-.2-.31-.42-.67-.67-1.11C2.44 6.78 2 5.65 2 5c0-2.2 2.02-4 4.5-4 1.22 0 2.36.42 3.22 1.19C10.55 2.94 11 3.94 11 5c0 .66-.44 1.78-.86 2.48zM4 14h5c-.23 1.14-1.3 2-2.5 2s-2.27-.86-2.5-2z"
              }}></path></svg></span>{`tip`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`For specific instructions on how to set environment variables visit, the following links:`}</p><ul parentName="div">
          <li parentName="ul"><a parentName="li" {...{
              "href": "https://www.architectryan.com/2018/08/31/how-to-change-environment-variables-on-windows-10/"
            }}>{`Set Environment Variables with Windows 10`}</a>{` `}</li>
          <li parentName="ul"><a parentName="li" {...{
              "href": "https://apple.stackexchange.com/questions/106778/how-do-i-set-environment-variables-on-os-x"
            }}>{`Set Environment Variables with MacOS`}</a></li>
          <li parentName="ul"><a parentName="li" {...{
              "href": "https://askubuntu.com/questions/58814/how-do-i-add-environment-variables"
            }}>{`Set Environment Variables with Linux`}</a></li>
        </ul></div></div>
    <h3 {...{
      "id": "using-the-configure-command"
    }}>{`Using the Configure Command`}</h3>
    <p>{`Testrunner Toolkit generally does a good job of detecting the presence of `}<inlineCode parentName="p">{`SAUCE_USERNAME`}</inlineCode>{` and `}<inlineCode parentName="p">{`SAUCE_ACCESS_KEY`}</inlineCode>{`, but to ensure you're properly authenticated, you can run the following optional command:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`saucectl configure
`}</code></pre>
    <p>{`This command prompts you to manually enter your credentials if it cannot detect any environment variables, and will generate a `}<inlineCode parentName="p">{`credentials.yml`}</inlineCode>{` file in a `}<inlineCode parentName="p">{`.sauce`}</inlineCode>{` directory in your home folder.`}</p>
    <div {...{
      "className": "admonition admonition-warning alert alert--danger"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M5.05.31c.81 2.17.41 3.38-.52 4.31C3.55 5.67 1.98 6.45.9 7.98c-1.45 2.05-1.7 6.53 3.53 7.7-2.2-1.16-2.67-4.52-.3-6.61-.61 2.03.53 3.33 1.94 2.86 1.39-.47 2.3.53 2.27 1.67-.02.78-.31 1.44-1.13 1.81 3.42-.59 4.78-3.42 4.78-5.56 0-2.84-2.53-3.22-1.25-5.61-1.52.13-2.03 1.13-1.89 2.75.09 1.08-1.02 1.8-1.86 1.33-.67-.41-.66-1.19-.06-1.78C8.18 5.31 8.68 2.45 5.05.32L5.03.3l.02.01z"
              }}></path></svg></span>{`Do NOT commit `}<inlineCode parentName="h5">{`credentials.yml`}</inlineCode></h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`It should go without saying, but do not publicly expose your `}<inlineCode parentName="p">{`credentials.yml`}</inlineCode>{` file over the internet. Make sure you add this file to your `}<inlineCode parentName="p">{`.gitignore`}</inlineCode>{` file, as you should only use it locally.`}</p></div></div>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
