};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">



    <p>{`This page provides details and explanations regarding the syntax and fields of the `}<a parentName="p" {...{
        "href": "/testrunner-toolkit/configuration"
      }}>{`Testrunner Toolkit Configuration`}</a>{`.`}</p>
    <h2 {...{
      "id": "full-examples"
    }}>{`Full Examples`}</h2>
    <Tabs defaultValue="cypress" values={[{
      label: 'Cypress',
      value: 'cypress'
    }, {
      label: 'Playwright',
      value: 'playwright'
    }, {
      label: 'TestCafe',
      value: 'testcafe'
    }]} mdxType="Tabs">
      <TabItem value="cypress" mdxType="TabItem">
        <p><small>See full example <a href="https://github.com/saucelabs/sauce-cypress-runner/blob/master/.saucetpl/.sauce/config.yml">here</a>.</small></p>
        <pre><code parentName="pre" {...{
            "className": "language-yaml"
          }}>{`apiVersion: v1alpha
kind: cypress
sauce:
  region: us-west-1
  metadata:
    name: Testing Cypress Support
    tags:
      - e2e
      - release team
      - other tag
    build: Release $CI_COMMIT_SHORT_SHA
cypress:
  version: ##VERSION##
  configFile: "cypress.json"  # We determine related files based on the location of the config file.
suites:
  - name: "saucy test"
    browser: "chrome"
    screenResolution: "2560x1600"  # Available resolutions on sauce for Windows: '800x600', '1024x768', '1152x864', '1280x768', '1280x800', '1280x960', '1280x1024', '1400x1050', '1440x900', '1600x1200', '1680x1050', '1920x1080', '1920x1200', '2560x1600'
    config:
      env:
        hello: world
      testFiles: [ "**/*.*" ] # Cypress native glob support.
`}</code></pre>
      </TabItem>
      <TabItem value="playwright" mdxType="TabItem">
        <p><small>See full example <a href="https://github.com/saucelabs/sauce-playwright-runner/blob/master/.saucetpl/.sauce/config.yml">here</a>.</small></p>
        <pre><code parentName="pre" {...{
            "className": "language-yaml"
          }}>{`apiVersion: v1alpha
kind: playwright
sauce:
  region: us-west-1
  concurrency: 1
  metadata:
    name: Testing Playwright Support
    tags:
      - e2e
    build: "$BUILD_ID"
playwright:
  version: ##VERSION##
  projectPath: tests/
docker:
  fileTransfer: mount
suites:
  - name: "saucy test"
    platformName: "Windows 10"
    testMatch: '**/*.js'

    params:
      browserName: "firefox"
      headful: false
      slowMo: 1000
`}</code></pre>
      </TabItem>
      <TabItem value="testcafe" mdxType="TabItem">
        <p><small>See full example <a href="https://github.com/saucelabs/sauce-testcafe-runner/blob/master/.saucetpl/.sauce/config.yml">here</a>.</small></p>
        <pre><code parentName="pre" {...{
            "className": "language-yaml"
          }}>{`apiVersion: v1alpha
metadata:
  name: Feature XYZ
  tags:
    - e2e
    - release team
    - other tag
  build: Release $CI_COMMIT_SHORT_SHA
files:
  - tests/example.test.js
suites:
  - name: "saucy test"
    match: ".*.(spec|test).[jt]s$"
image:
  base: saucelabs/stt-testcafe-node
  version: ##VERSION##
sauce:
  region: us-west-1
`}</code></pre>
      </TabItem>
    </Tabs>
    <h2 {...{
      "id": "apiversion"
    }}><inlineCode parentName="h2">{`apiVersion`}</inlineCode></h2>
    <p><strong parentName="p">{`Description`}</strong>{`: Version of `}<inlineCode parentName="p">{`saucectl`}</inlineCode>{` API.`}</p>
    <p><strong parentName="p">{`Type`}</strong>{`: `}<em parentName="p">{`string`}</em></p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`apiVersion: v1alpha
`}</code></pre>
    <h2 {...{
      "id": "kind"
    }}><inlineCode parentName="h2">{`kind`}</inlineCode></h2>
    <p><strong parentName="p">{`Description`}</strong>{`: The kind of tests (framework) you wish to run.`}</p>
    <p><strong parentName="p">{`Type`}</strong>{`: `}<em parentName="p">{`string`}</em></p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`kind: < cypress | playwright | testcafe >
`}</code></pre>
    <h2 {...{
      "id": "sauce"
    }}><inlineCode parentName="h2">{`sauce`}</inlineCode></h2>
    <p><strong parentName="p">{`Description`}</strong>{`: The parent field containing all details related to the Sauce Labs platform.`}</p>
    <p><strong parentName="p">{`Type`}</strong>{`: NA`}</p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`sauce:
  region: < us-west-1 | eu-central-1 >
  metadata:
    name: Testing Cypress Support
    tags:
      - e2e
      - release team
      - other tag
    build: Release $CI_COMMIT_SHORT_SHA
`}</code></pre>
    <h3 {...{
      "id": "region"
    }}><inlineCode parentName="h3">{`region`}</inlineCode></h3>
    <p><strong parentName="p">{`Description`}</strong>{`: Geographical region of the desired Sauce Labs data center.`}</p>
    <p><strong parentName="p">{`Type`}</strong>{`: `}<em parentName="p">{`string`}</em></p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`  region: < us-west-1 | eu-central-1 >
`}</code></pre>
    <h3 {...{
      "id": "metadata"
    }}><inlineCode parentName="h3">{`metadata`}</inlineCode></h3>
    <p><strong parentName="p">{`Description`}</strong>{`: Data specific to the test execution details (i.e. `}<inlineCode parentName="p">{`name`}</inlineCode>{`, `}<inlineCode parentName="p">{`tags`}</inlineCode>{`, `}<inlineCode parentName="p">{`build`}</inlineCode>{`, etc.)`}</p>
    <p><strong parentName="p">{`Type`}</strong>{`: NA`}</p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`  metadata:
    name: Testing Cypress Support
    tags:
      - e2e
      - release team
      - other tag
    build: Release $CI_COMMIT_SHORT_SHA
`}</code></pre>
    <h2 {...{
      "id": "docker"
    }}><inlineCode parentName="h2">{`docker`}</inlineCode></h2>
    <p><strong parentName="p">{`Description`}</strong>{`: Details specific to the desired `}<a parentName="p" {...{
        "href": "https://hub.docker.com/u/saucelabs"
      }}>{`Sauce Labs docker images`}</a>{`.`}</p>
    <p><strong parentName="p">{`Type`}</strong>{`: NA`}</p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`docker:
  fileTransfer: mount
  image: saucelabs/stt-cypress-mocha-node:vX.X.X
`}</code></pre>
    <h3 {...{
      "id": "filetransfer"
    }}><inlineCode parentName="h3">{`fileTransfer`}</inlineCode></h3>
    <p><strong parentName="p">{`Description`}</strong>{`: Method in which to transfer test files into the docker container. There are two options:`}</p>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`mount`}</inlineCode>{` : Default method; mounts files and folders into the docker container. Changes to these files and folders will be reflected on the host (and vice a versa).`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`copy`}</inlineCode>{` : Copies files and folders into the docker container. If you run into permission issues, either due to docker or host settings, `}<inlineCode parentName="li">{`copy`}</inlineCode>{` is the advised use case.`}
        <blockquote parentName="li">
          <p parentName="blockquote">{`See the Docker documentation to read more about the copy convention (`}<a parentName="p" {...{
              "href": "https://docs.docker.com/engine/reference/commandline/cp/"
            }}><inlineCode parentName="a">{`docker cp`}</inlineCode></a>{` | `}<a parentName="p" {...{
              "href": "https://docs.docker.com/engine/reference/builder/#copy"
            }}><inlineCode parentName="a">{`COPY`}</inlineCode></a>{`).`}</p>
        </blockquote>
      </li>
    </ul>
    <p><strong parentName="p">{`Type`}</strong>{`: `}<em parentName="p">{`string`}</em></p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`  fileTransfer: < mount | copy >
`}</code></pre>
    <h3 {...{
      "id": "image"
    }}><inlineCode parentName="h3">{`image`}</inlineCode></h3>
    <p><strong parentName="p">{`Description`}</strong>{`: The chosen docker image, `}<inlineCode parentName="p">{`name`}</inlineCode>{` and version `}<inlineCode parentName="p">{`tag`}</inlineCode>{`, in which to run tests.`}</p>
    <p><strong parentName="p">{`Type`}</strong>{`: `}<em parentName="p">{`string`}</em></p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`  image:
    name: saucelabs/< stt-cypress-mocha-node | stt-playwright-node | stt-testcafe-node >:< vX.X.X >
`}</code></pre>
    <blockquote>
      <p parentName="blockquote">{`WARNING: using the `}<inlineCode parentName="p">{`latest`}</inlineCode>{` tag for docker images is dangerous. For further information, read `}<a parentName="p" {...{
          "href": "https://vsupalov.com/docker-latest-tag/#:~:text=You%20should%20avoid%20using%20the,apart%20from%20the%20image%20ID."
        }}>{`this article`}</a>{`.`}</p>
    </blockquote>
    <h2 {...{
      "id": "cypress"
    }}><inlineCode parentName="h2">{`cypress`}</inlineCode></h2>
    <p><small><Highlight color="#25c2a0" mdxType="Highlight">cypress only</Highlight></small></p>
    <p><strong parentName="p">{`Description`}</strong>{`: Details specific to the `}<inlineCode parentName="p">{`cypress`}</inlineCode>{` configuration.`}</p>
    <p><strong parentName="p">{`Type`}</strong>{`: NA`}</p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`cypress:
  configFile: "cypress.json"
`}</code></pre>
    <h3 {...{
      "id": "configfile"
    }}><inlineCode parentName="h3">{`configFile`}</inlineCode></h3>
    <p><small><Highlight color="#25c2a0" mdxType="Highlight">cypress only</Highlight></small></p>
    <p><strong parentName="p">{`Description`}</strong>{`: The designated `}<inlineCode parentName="p">{`cypress`}</inlineCode>{` configuration file. `}<inlineCode parentName="p">{`saucectl`}</inlineCode>{` determines related files based on the location of the config file. By default `}<inlineCode parentName="p">{`saucectl`}</inlineCode>{` defers to the test file location defined in `}<inlineCode parentName="p">{`cypress.json`}</inlineCode>{`.`}</p>
    <p><strong parentName="p">{`Type`}</strong>{`: `}<em parentName="p">{`string`}</em></p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`  configFile: "cypress.json"
`}</code></pre>
    <blockquote>
      <p parentName="blockquote">{`For further information regarding cypress configurations, please consult the `}<a parentName="p" {...{
          "href": "https://docs.cypress.io/guides/references/configuration.html#Options"
        }}>{`Cypress documentation`}</a>{`.`}</p>
    </blockquote>
    <h2 {...{
      "id": "suites"
    }}><inlineCode parentName="h2">{`suites`}</inlineCode></h2>
    <p><strong parentName="p">{`Description`}</strong>{`: Field for defining test suite details such as the suite `}<inlineCode parentName="p">{`name`}</inlineCode>{`, desired `}<inlineCode parentName="p">{`browser`}</inlineCode>{`
/ `}<inlineCode parentName="p">{`platformName`}</inlineCode>{`, and `}<inlineCode parentName="p">{`config`}</inlineCode>{`.`}</p>
    <p><strong parentName="p">{`Type`}</strong>{`: NA`}</p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`suites:
  - name: "saucy test"
    browser: "chrome"
    platformName: "Windows 10"
    screenResolution: "2560x1600"
    config:
      env:
        hello: world
      testFiles: [ "**/*.*" ] # Cypress native glob support.
`}</code></pre>
    <h3 {...{
      "id": "name"
    }}><inlineCode parentName="h3">{`name`}</inlineCode></h3>
    <p><strong parentName="p">{`Description`}</strong>{`: Name of the test suite.`}</p>
    <p><strong parentName="p">{`Type`}</strong>{`: `}<em parentName="p">{`string`}</em></p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`  - name: "saucy test"
`}</code></pre>
    <h3 {...{
      "id": "match"
    }}><inlineCode parentName="h3">{`match`}</inlineCode></h3>
    <p><strong parentName="p">{`Description`}</strong>{`: Regular expression for test file names.`}</p>
    <p><strong parentName="p">{`Type`}</strong>{`: `}<em parentName="p">{`string`}</em></p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`    match: ".*.(spec|test).js$"
`}</code></pre>
    <h3 {...{
      "id": "browser"
    }}><inlineCode parentName="h3">{`browser`}</inlineCode></h3>
    <p><strong parentName="p">{`Description`}</strong>{`: Browser in which the test runs.`}</p>
    <p><strong parentName="p">{`Type`}</strong>{`: `}<em parentName="p">{`string`}</em></p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`    browser: "chrome"
`}</code></pre>
    <h3 {...{
      "id": "platformname"
    }}><inlineCode parentName="h3">{`platformName`}</inlineCode></h3>
    <p><strong parentName="p">{`Description`}</strong>{`: Operating system on which the browser and test runs.`}</p>
    <p><strong parentName="p">{`Type`}</strong>{`: `}<em parentName="p">{`string`}</em></p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`    platformName: "Windows 10"
`}</code></pre>
    <h3 {...{
      "id": "screenresolution"
    }}><inlineCode parentName="h3">{`screenResolution`}</inlineCode></h3>
    <p><small><Highlight color="#25c2a0" mdxType="Highlight">cypress only</Highlight></small></p>
    <p><strong parentName="p">{`Description`}</strong>{`: Field where you can change the browser window screen resolution.`}</p>
    <p><strong parentName="p">{`Type`}</strong>{`: `}<em parentName="p">{`string`}</em></p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`    screenResolution: "2560x1600"  # Available resolutions on sauce for Windows: '800x600', '1024x768', '1152x864', '1280x768', '1280x800', '1280x960', '1280x1024', '1400x1050', '1440x900', '1600x1200', '1680x1050', '1920x1080', '1920x1200', '2560x1600'
`}</code></pre>
    <h3 {...{
      "id": "config"
    }}><inlineCode parentName="h3">{`config`}</inlineCode></h3>
    <p><small><Highlight color="#25c2a0" mdxType="Highlight">cypress only</Highlight></small></p>
    <p><strong parentName="p">{`Description`}</strong>{`: Details specific to the test configuration, for example: `}</p>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`testFiles`}</inlineCode>{` ( `}<em parentName="li">{`string`}</em>{` | `}<em parentName="li">{`regex`}</em>{` ): the specific location of test files`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`env`}</inlineCode>{` ( `}<em parentName="li">{`string`}</em>{` | `}<em parentName="li">{`int`}</em>{` | `}<em parentName="li">{`float`}</em>{` | `}<em parentName="li">{`boolean`}</em>{` ) any ephemeral/environment variables.`}</li>
    </ul>
    <p><strong parentName="p">{`Type`}</strong>{`: NA`}</p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`    config:
      env:
        hello: world
      testFiles: [ "**/*.*" ]
`}</code></pre>
    <h3 {...{
      "id": "params"
    }}><inlineCode parentName="h3">{`params`}</inlineCode></h3>
    <p><small><Highlight color="#44ba4a" mdxType="Highlight">playwright only</Highlight></small></p>
    <p><strong parentName="p">{`Description`}</strong>{`: This field is for specific test run parameters, for example:`}</p>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`browserName`}</inlineCode>{` ( `}<em parentName="li">{`string`}</em>{` ) : the browser in which to run tests `}</li>
      <li parentName="ul"><inlineCode parentName="li">{`headful`}</inlineCode>{` ( `}<em parentName="li">{`boolean`}</em>{` ) : whether to run browsers in headless mode`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`sloMo`}</inlineCode>{` ( `}<em parentName="li">{`int`}</em>{` ) : whether to implement artificially slow load times in milliseconds`}</li>
    </ul>
    <p><strong parentName="p">{`Type`}</strong>{`: NA`}</p>
    <p><strong parentName="p">{`Example`}</strong>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`    params:
      browserName: "firefox"
      headful: false
      slowMo: 1000
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
