  }, {
    value: '<code>run</code>',
    id: 'run',
    children: []
  }]
}, {
  value: 'Flags',
  id: 'flags',
  children: [{
    value: '<code>config</code>',
    id: 'config',
    children: []
  }, {
    value: '<code>env</code>',
    id: 'env',
