const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`This page provides information for `}<inlineCode parentName="p">{`saucectl`}</inlineCode>{`, the command line interface of the Sauce Labs Testrunner Toolkit.`}</p>
    <h2 {...{
      "id": "development-requirements"
    }}>{`Development Requirements`}</h2>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://git-scm.com/downloads"
        }}>{`Git`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://golang.org/"
        }}>{`Go`}</a>{` (v1.14 or higher)`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://brew.sh/"
        }}>{`Homebrew`}</a>{` (v2.2.13 or higher)`}</li>
    </ul>
    <h2 {...{
      "id": "install"
    }}>{`Install`}</h2>
    <p>{`Download `}<inlineCode parentName="p">{`saucectl`}</inlineCode>{` from `}<a parentName="p" {...{
        "href": "https://github.com/saucelabs/saucectl"
      }}>{`GitHub`}</a>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-sh"
      }}>{`$ git clone https://github.com/saucelabs/saucectl.git
`}</code></pre>
    <p>{`Run the following `}<inlineCode parentName="p">{`make`}</inlineCode>{` command to install all dependencies:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`$ make install
`}</code></pre>
    <h2 {...{
      "id": "build"
    }}>{`Build`}</h2>
    <p>{`To build the project run the following command:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`$ make build
`}</code></pre>
    <h2 {...{
      "id": "test"
    }}>{`Test`}</h2>
    <p>{`To execute unit tests run the following command:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`$ make test
`}</code></pre>
    <h2 {...{
      "id": "commands"
    }}>{`Commands`}</h2>
    <h3 {...{
      "id": "new"
    }}><inlineCode parentName="h3">{`new`}</inlineCode></h3>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`saucectl new
`}</code></pre>
    <p>{`This command will ask you to choose one of the frameworks:`}</p>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://github.com/puppeteer/puppeteer"
        }}>{`Puppeteer`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://github.com/microsoft/playwright"
        }}>{`Playwright`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://github.com/DevExpress/testcafe"
        }}>{`TestCafe`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://github.com/cypress-io/cypress"
        }}>{`Cypress`}</a></li>
    </ul>
    <p>{`After which, a `}<inlineCode parentName="p">{`./sauce/config.yml`}</inlineCode>{` file and an example test under the tests directory will be created.`}</p>
    <h3 {...{
      "id": "run"
    }}><inlineCode parentName="h3">{`run`}</inlineCode></h3>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`saucectl run
`}</code></pre>
    <p>{`This command will run the test based on the `}<inlineCode parentName="p">{`./.sauce/config.yml`}</inlineCode>{` file.`}</p>
    <h2 {...{
      "id": "flags"
    }}>{`Flags`}</h2>
    <h3 {...{
      "id": "config"
    }}><inlineCode parentName="h3">{`config`}</inlineCode></h3>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`saucectl run --config <path>
`}</code></pre>
    <p>{`Using the `}<inlineCode parentName="p">{`--config`}</inlineCode>{` flag will run the tests specified by that config file.`}</p>
    <h3 {...{
      "id": "env"
    }}><inlineCode parentName="h3">{`env`}</inlineCode></h3>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`saucectl run --env <key>=value1> --env <key2>=<value2> ...
`}</code></pre>
    <p>{`Using the `}<inlineCode parentName="p">{`--env`}</inlineCode>{` flag will define environment variables that are then available for use by the test framework.`}</p>
    <h3 {...{
      "id": "parallel"
    }}><inlineCode parentName="h3">{`parallel`}</inlineCode></h3>
    <pre><code parentName="pre" {...{
        "className": "language-sh"
      }}>{`saucectl run --parallel=<true|false>
`}</code></pre>
    <p>{`Using the `}<inlineCode parentName="p">{`--parallel`}</inlineCode>{` flag allows the parallelization of tests across machines to be
turned on/off. `}</p>
    <p><inlineCode parentName="p">{`saucectl`}</inlineCode>{` will use CI provider specific clues from the environment and `}<inlineCode parentName="p">{`saucectl`}</inlineCode>{` config
file to generate a `}<inlineCode parentName="p">{`build ID`}</inlineCode>{`. This `}<inlineCode parentName="p">{`build ID`}</inlineCode>{` is used a grouping mechanism to
synchronize the different machines that are running in the same pipeline to distribute
the tests. `}</p>
    <p><inlineCode parentName="p">{`saucectl`}</inlineCode>{` currently uses the following CI environment variables to generate a build ID.`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "center"
          }}>{`CI`}</th>
          <th parentName="tr" {...{
            "align": "center"
          }}>{`Environment Variables`}</th>
          <th parentName="tr" {...{
            "align": "center"
          }}>{`Current Limitations`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "center"
          }}>{`GitHub`}</td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`GITHUB_WORKFLOW, GITHUB_RUN_ID`}</td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Unable to re-run jobs. Must trigger a new pipeline.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "center"
          }}>{`GitLab`}</td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`CI_PIPELINE_ID, CI_JOB_STAGE`}</td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Unable to re-run jobs. Must trigger a new pipeline.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Jenkins`}</td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`BUILD_NUMBER`}</td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`None`}</td>
        </tr>
      </tbody>
    </table>
    <p>{`The current parallelization feature is `}<em parentName="p">{`highly experimental`}</em>{` and may have limitations
with certain CI providers.`}</p>
    <p>{`If your CI provider is not listed here, you will have to specify your own `}<inlineCode parentName="p">{`build ID`}</inlineCode>{`.
Please consult the `}<a parentName="p" {...{
        "href": "#ci-build-id"
      }}><inlineCode parentName="a">{`ci-build-id`}</inlineCode></a>{` flag for this option.`}</p>
    <h3 {...{
      "id": "ci-build-id"
    }}><inlineCode parentName="h3">{`ci-build-id`}</inlineCode></h3>
    <pre><code parentName="pre" {...{
        "className": "language-sh"
      }}>{`saucectl run --ci-build-id <value>
`}</code></pre>
    <p>{`Using the `}<inlineCode parentName="p">{`--ci-build-id`}</inlineCode>{` flag will override the build ID that is otherwise determined
based on the CI provider. The config file hash will still be used in addition to this
provided CI build ID.`}</p>
    <h3 {...{
      "id": "region"
    }}><inlineCode parentName="h3">{`region`}</inlineCode></h3>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`saucectl run --region <region>
`}</code></pre>
    <p>{`Using the --region flag will set the Sauce Labs region for the test execution. The region corresponds to the available regions at `}<a parentName="p" {...{
        "href": "https://app.saucelabs.com"
      }}>{`saucelabs.com`}</a>{` and affects where your job information and assets are going to be stored.`}</p>
    <h3 {...{
      "id": "timeout"
    }}><inlineCode parentName="h3">{`timeout`}</inlineCode></h3>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`saucectl run --timeout <seconds>
`}</code></pre>
    <p>{`Using the `}<inlineCode parentName="p">{`--timeout`}</inlineCode>{` flag will set the test timeout for the `}<a parentName="p" {...{
        "href": "/products/testrunner-toolkit/preparation#automation-framework-examples"
      }}>{`Testrunner framework`}</a>{`.`}</p>
    <h2 {...{
      "id": "licensing"
    }}>{`Licensing`}</h2>
    <p><inlineCode parentName="p">{`saucectl`}</inlineCode>{` is licensed under the Apache License, Version 2.0. See `}<a parentName="p" {...{
        "href": "https://github.com/saucelabs/saucectl/blob/master/LICENSE"
      }}>{`LICENSE`}</a>{` for the full license text.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
