const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`For information on native mobile app test automation using Sauce Labs emulators, simulators, and real devices, see the following sections:`}</p>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://wiki.saucelabs.com/pages/viewpage.action?pageId=48365795#MobileApplicationTesting-EnvironmentConfiguration"
        }}>{`Environment Configuration`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://wiki.saucelabs.com/pages/viewpage.action?pageId=48365795#MobileApplicationTesting-GettingStarted"
        }}>{`Getting Started`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://wiki.saucelabs.com/pages/viewpage.action?pageId=48365795#MobileApplicationTesting-MoreInformation"
        }}>{`More Information`}</a></li>
    </ul>
    <h2 {...{
      "id": "system-requirements-and-environment-configuration"
    }}>{`System Requirements and Environment Configuration`}</h2>
    <p>{`For information on platform/support requirements and test environment configuration, see `}<a parentName="p" {...{
        "href": "/mobile-apps/automated-testing/devices"
      }}>{`Choosing a Mobile Device`}</a>{`.`}</p>
    <h2 {...{
      "id": "getting-started"
    }}>{`Getting Started`}</h2>
    <p>{`A general process outline for automated testing goes like this:`}</p>
    <ol>
      <li parentName="ol">{`Upload the app you want to test on `}<a parentName="li" {...{
          "href": "https://wiki.saucelabs.com/display/DOCS/Uploading+and+Accessing+Applications+with+Real+Devices"
        }}>{`real devices`}</a>{` or `}<a parentName="li" {...{
          "href": "https://wiki.saucelabs.com/display/DOCS/Uploading+your+Application+to+Emulators+and+Simulators"
        }}>{`virtual devices (emulators and simulators)`}</a>{` to a location where Sauce Labs can retrieve it; for example, AWS S3, GitHub, Dropbox, `}<a parentName="li" {...{
          "href": "https://wiki.saucelabs.com/display/DOCS/Uploading+your+Application+to+Sauce+Storage"
        }}>{`Sauce Storage`}</a>{`, etc. Please keep in mind that if the application you want to test isn't publicly available / behind a firewall, you'll need to set up a secure `}<a parentName="li" {...{
          "href": "https://wiki.saucelabs.com/display/DOCS/Sauce+Connect+Proxy"
        }}>{`Sauce Connect Proxy Tunnel`}</a>{` in order for Sauce Labs to connect to the application.`}</li>
      <li parentName="ol">{`Update your test script with your `}<a parentName="li" {...{
          "href": "https://wiki.saucelabs.com/display/DOCS/Best+Practice%3A+Use+Environment+Variables+for+Authentication+Credentials"
        }}>{`Sauce Labs credentials`}</a>{` and the path to the application you want to test, as shown in the example scripts located here:`}
        <ol parentName="li">
          <li parentName="ol"><a parentName="li" {...{
              "href": "https://wiki.saucelabs.com/display/DOCS/Example+Appium+Scripts+for+iOS+Mobile+Application+Tests"
            }}>{`Example Appium Scripts for iOS Mobile Application Tests`}</a></li>
          <li parentName="ol"><a parentName="li" {...{
              "href": "https://wiki.saucelabs.com/display/DOCS/Example+Appium+Scripts+for+Android+Mobile+Application+Tests"
            }}>{`Example Appium Scripts for Android Mobile Application Tests`}</a></li>
        </ol>
      </li>
      <li parentName="ol">{`Set `}<a parentName="li" {...{
          "href": "https://wiki.saucelabs.com/display/DOCS/Desired+Capabilities+Required+for+Selenium+and+Appium+Tests"
        }}>{`the desired capabilities of your test`}</a>{` for the device/operating system you want to test against, and the path to your application. The topics in `}<a parentName="li" {...{
          "href": "https://wiki.saucelabs.com/display/DOCS/Test+Configuration+and+Annotation"
        }}>{`Test Configuration and Annotation`}</a>{` describe the various options for desired capabilities, or you can use our `}<a parentName="li" {...{
          "href": "https://wiki.saucelabs.com/display/DOCS/Platform+Configurator"
        }}>{`Platform Configurator`}</a>{` to set up the desired capabilities of your test.`}</li>
    </ol>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
