export const toc = [{
  value: 'What You&#39;ll Need',
  id: 'what-youll-need',
  children: []
}, {
  value: 'Creating the Post-Build Script',
  id: 'creating-the-post-build-script',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`In order to upload your apps from Microsoft App Center to a Sauce Labs data center you must create a `}<a parentName="p" {...{
        "href": "https://docs.microsoft.com/en-us/appcenter/build/custom/scripts/#post-build"
      }}>{`post-build script`}</a>{` in Microsoft App Center.`}</p>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`NOTE`}</strong>{`: if you're looking for the legacy solution, see `}<a parentName="p" {...{
          "href": "https://wiki.saucelabs.com/pages/viewpage.action?pageId=110205512"
        }}>{`Legacy Platform App Center Integration`}</a>{` page.`}</p>
    </blockquote>
    <h2 {...{
      "id": "what-youll-need"
    }}>{`What You'll Need`}</h2>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://docs.microsoft.com/en-us/appcenter/"
        }}>{`Microsoft App Center Account`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://docs.microsoft.com/en-us/appcenter/build/#getting-started"
        }}>{`A Project Source Code Repository`}</a></li>
    </ul>
    <h2 {...{
      "id": "creating-the-post-build-script"
    }}>{`Creating the Post-Build Script`}</h2>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`Once you've linked your project source code to your app center project, create a script called `}<inlineCode parentName="p">{`appcenter-post-build.sh`}</inlineCode>{` and add it to your project source code repository.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`If you're testing a cross-platform application (e.g., a react-native), add the following environment variables `}<inlineCode parentName="p">{`APP_NAME`}</inlineCode>{` and `}<inlineCode parentName="p">{`BUILD_NAME`}</inlineCode>{`.`}</p>
      </li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`#!/usr/bin/env bash
APP_NAME="your.app.name"
# You can find this name in the build logs of a previous project
# for now we create an empty variable and change it based on the
# project that is using this script
BUILD_NAME="your.build.name"
`}</code></pre>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`NOTE`}</strong>{`: If you're building a single project (iOS or Android), ignore steps 2-4 and skip to step 5.`}</p>
    </blockquote>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Set the correct app name based on the application platform.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`if [[ "$APPCENTER_XCODE_PROJECT" ]]; then
    APP_NAME="iOS.SauceLabs.Mobile.Sample.app.ipa"
    BUILD_NAME="SwagLabsMobileApp.ipa"
else
    APP_NAME="Android.SauceLabs.Mobile.Sample.app.apk"
    BUILD_NAME="app-release.apk"
fi
`}</code></pre>
    <ol {...{
      "start": 4
    }}>
      <li parentName="ol">{`You can optionally set a console message to appear in your App Center logs.`}</li>
    </ol>
    <pre><code parentName="pre" {...{}}>{`echo "**************** PUBLISH APP TO SAUCELABS WITH THIS DATA ******************"
echo "APP NAME                => $APP_NAME"
echo "BUILD NAME              => $BUILD_NAME"
echo "OUTPUT DIRECTORY        => $APPCENTER_OUTPUT_DIRECTORY"
echo "PAYLOAD                 => $APPCENTER_OUTPUT_DIRECTORY/$BUILD_NAME"
`}</code></pre>
    <ol {...{
      "start": 5
    }}>
      <li parentName="ol">{`Now you can add the following command to push to the relevant data center that contains your target real device.`}</li>
    </ol>
    <p><strong parentName="p">{`Push to US Data Center`}</strong></p>
    <pre><code parentName="pre" {...{}}>{`curl \\
  -F "payload=@$APPCENTER_OUTPUT_DIRECTORY/$BUILD_NAME" \\
  -F name=$APP_NAME \\
  -u "$SAUCE_USERNAME:$SAUCE_ACCESS_KEY"  'https://api.us-west-1.saucelabs.com/v1/storage/upload'
`}</code></pre>
    <p><strong parentName="p">{`Push to EU Data Center`}</strong></p>
    <pre><code parentName="pre" {...{}}>{`curl \\
  -F "payload=@$APPCENTER_OUTPUT_DIRECTORY/$BUILD_NAME" \\
  -F name=$APP_NAME \\
  -u "$SAUCE_USERNAME:$SAUCE_ACCESS_KEY"  'https://api.eu-central-1.saucelabs.com/v1/storage/upload'
`}</code></pre>
    <div {...{
      "className": "admonition admonition-tip alert alert--success"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.5 0C3.48 0 1 2.19 1 5c0 .92.55 2.25 1 3 1.34 2.25 1.78 2.78 2 4v1h5v-1c.22-1.22.66-1.75 2-4 .45-.75 1-2.08 1-3 0-2.81-2.48-5-5.5-5zm3.64 7.48c-.25.44-.47.8-.67 1.11-.86 1.41-1.25 2.06-1.45 3.23-.02.05-.02.11-.02.17H5c0-.06 0-.13-.02-.17-.2-1.17-.59-1.83-1.45-3.23-.2-.31-.42-.67-.67-1.11C2.44 6.78 2 5.65 2 5c0-2.2 2.02-4 4.5-4 1.22 0 2.36.42 3.22 1.19C10.55 2.94 11 3.94 11 5c0 .66-.44 1.78-.86 2.48zM4 14h5c-.23 1.14-1.3 2-2.5 2s-2.27-.86-2.5-2z"
              }}></path></svg></span>{`tip`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`If you need to retrieve a specific build, or information regarding the build that ran during this operation, you can use the `}<a parentName="p" {...{
            "href": "https://wiki.saucelabs.com/pages/viewpage.action?pageId=102721137"
          }}>{`App Storage`}</a>{` API.`}</p></div></div>
    <ol {...{
      "start": 6
    }}>
      <li parentName="ol">{`To view the full `}<strong parentName="li">{`App Center Post-Build Script`}</strong>{`, see `}<a parentName="li" {...{
          "href": "https://github.com/saucelabs/sample-app-mobile/blob/master/appcenter-post-build.sh"
        }}>{`Sauce Labs sample mobile app repo`}</a>{`.`}</li>
    </ol>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
