  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'protecting-test-log-credentials',
	title: 'Protecting Test Log Credentials',
	sidebar_label: 'Protecting Test Log Credentials'
};
export const metadata = {
  "unversionedId": "basics/best-practices/protecting-test-log-credentials",
  "id": "basics/best-practices/protecting-test-log-credentials",
  "isDocsHomePage": false,
  "title": "Protecting Test Log Credentials",
  "description": "Sauce Labs test logs are securely stored, protecting them from external access. However, there are still some groups that can see test logs, including Sauce Support, your parent account, and other accounts in your company (depending on test privacy settings).",
  "source": "@site/docs/basics/best-practices/protecting-test-log-credentials.md",
  "slug": "/basics/best-practices/protecting-test-log-credentials",
  "permalink": "/basics/best-practices/protecting-test-log-credentials",
