	id: 'typescript',
	title: 'Using TypeScript Tests',
	sidebar_label: 'Using TypeScript'
};
export const metadata = {
  "unversionedId": "testrunner-toolkit/typescript",
  "id": "testrunner-toolkit/typescript",
  "isDocsHomePage": false,
  "title": "Using TypeScript Tests",
  "description": "If you wrote your automated tests using TypeScript in either Playwright, Puppeteer, or Cypress, you need to transpile your Typescript files to Javascript before running them with Testrunner Toolkit.",
  "source": "@site/docs/testrunner-toolkit/typescript.md",
  "slug": "/testrunner-toolkit/typescript",
  "permalink": "/testrunner-toolkit/typescript",
