export const toc = [{
  value: 'What You&#39;ll Need',
  id: 'what-youll-need',
  children: []
}, {
  value: 'The Problem',
  id: 'the-problem',
  children: []
}, {
  value: 'The Solution',
  id: 'the-solution',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`If you wrote your automated tests using TypeScript in either Playwright, Puppeteer, or Cypress, you need to transpile your Typescript files to Javascript before running them with Testrunner Toolkit.`}</p>
    <h2 {...{
      "id": "what-youll-need"
    }}>{`What You'll Need`}</h2>
    <p>{`Refer to the requirements listed on the `}<a parentName="p" {...{
        "href": "/testrunner-toolkit/installation"
      }}>{`Installation`}</a>{` page.`}</p>
    <h2 {...{
      "id": "the-problem"
    }}>{`The Problem`}</h2>
    <p>{`Consider the `}<inlineCode parentName="p">{`tests`}</inlineCode>{` directory structure below:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`.
└── tests/
    ├── test.one.spec.ts
    ├── test.two.spec.ts
    ├── test.three.spec.ts
    └── tsconfig.json
`}</code></pre>
    <p>{`Except for the TestCafe image, these `}<inlineCode parentName="p">{`.ts`}</inlineCode>{` files cannot run directly on any Testrunner Toolkit images. Therefore in order to make theses test run correctly you must transpile them JavaScript.`}</p>
    <h2 {...{
      "id": "the-solution"
    }}>{`The Solution`}</h2>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`Ensure you already have `}<inlineCode parentName="p">{`typescript`}</inlineCode>{` installed:`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash"
          }}>{`npm install -g typescript
`}</code></pre>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Review your `}<inlineCode parentName="p">{`tsconfig.json`}</inlineCode>{` to ensure you've set the `}<inlineCode parentName="p">{`compilerOptions`}</inlineCode>{` appropriately.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Run the TypeScript compiler like so:`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash"
          }}>{`tsc --project ./tests/tsconfig.json
`}</code></pre>
        <p parentName="li">{`Below is a default output example:`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash"
          }}>{`└── tests/
    ├── test.one.spec.ts
    ├── test.one.spec.js
    ├── test.two.spec.ts
    ├── test.two.spec.js
    ├── test.three.spec.ts
    ├── test.three.spec.js
    └── tsconfig.json
`}</code></pre>
        <blockquote parentName="li">
          <p parentName="blockquote">{`WARNING: the TypeScript compiler will behave in a non-deterministic manner if the `}<inlineCode parentName="p">{`tsconfig.json`}</inlineCode>{` is poorly configured. `}</p>
          <p parentName="blockquote">{`For more information on how to properly configure `}<inlineCode parentName="p">{`tsconfig.json`}</inlineCode>{` please visit the `}<a parentName="p" {...{
              "href": "https://www.typescriptlang.org/docs/handbook/migrating-from-javascript.html#writing-a-configuration-file"
            }}>{`documentation`}</a>{`.`}</p>
        </blockquote>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Next, edit the `}<inlineCode parentName="p">{`files`}</inlineCode>{` and `}<inlineCode parentName="p">{`suites`}</inlineCode>{` fields in `}<inlineCode parentName="p">{`.sauce/config.yml`}</inlineCode>{` in order to ignore the `}<inlineCode parentName="p">{`.ts`}</inlineCode>{` files and instead place the `}<inlineCode parentName="p">{`.js`}</inlineCode>{` files inside the Testrunner Toolkit container:`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-yaml"
          }}>{`files:
  - tests/
suites:
  - name: "basic test"
    match: ".*.(spec|test).js"
`}</code></pre>
        <p parentName="li">{`By default `}<inlineCode parentName="p">{`saucectl`}</inlineCode>{` will pickup any `}<inlineCode parentName="p">{`.js`}</inlineCode>{` files located in the designated directory, however with the `}<inlineCode parentName="p">{`suites`}</inlineCode>{` field you can set more granular control with regular expressions.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Finally, run `}<inlineCode parentName="p">{`saucectl`}</inlineCode>{` to execute your TypeScript tests:`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash"
          }}>{`saucectl run -c .sauce/config.yml
`}</code></pre>
        <p parentName="li">{` For further information, please refer to the working example of this TypeScript demonstration in the `}<a parentName="p" {...{
            "href": "https://github.com/saucelabs/sauce-puppeteer-runner/tree/master/tests/fixtures/typescript"
          }}>{`Sauce Labs Puppeteer Runner`}</a>{` repository.`}</p>
      </li>
    </ol>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
