import useBaseUrl from '@docusaurus/useBaseUrl';
export const toc = [{
  value: 'Assigning a User to a Team',
  id: 'assigning-a-user-to-a-team',
  children: []
}, {
  value: 'Removing Users from Teams',
  id: 'removing-users-from-teams',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p><button class="badge-blue">ENTERPRISE PLANS ONLY</button></p>
If you are an organization admin, you can assign users to and remove users from a team.
    <h2 {...{
      "id": "assigning-a-user-to-a-team"
    }}>{`Assigning a User to a Team`}</h2>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`In Sauce Labs, click `}<strong parentName="p">{`Account`}</strong>{` and then click `}<strong parentName="p">{`Team Management`}</strong>{`.`}</p>
        <img src={useBaseUrl('img/team-mgmt/team-mgmt-nav.jpg')} alt="Team management navigation" width="400" />
      </li>
      <li parentName="ol">
        <p parentName="li">{`On the `}<strong parentName="p">{`Users`}</strong>{` tab, select the checkbox of the user(s) you want to assign or remove.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Next to `}<strong parentName="p">{`Users Selected`}</strong>{`, in the `}<strong parentName="p">{`Action`}</strong>{` dropdown, click `}<strong parentName="p">{`Team Assignment`}</strong>{`.`}</p>
        <img src={useBaseUrl('img/team-mgmt/assign-users-to-team.jpg')} alt="Assign users to a team" />
      </li>
      <li parentName="ol">
        <p parentName="li">{`In the `}<strong parentName="p">{`Team Assignment`}</strong>{` box, in the `}<strong parentName="p">{`Select team`}</strong>{` dropdown, select the team to assign the user to.`}</p>
      </li>
    </ol>
    <p>{`A user can only be assigned to one team; changing a user's team assignment will remove the association with the previous team.`}</p>
    <h2 {...{
      "id": "removing-users-from-teams"
    }}>{`Removing Users from Teams`}</h2>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`In Sauce Labs, click `}<strong parentName="p">{`Account`}</strong>{` and then click `}<strong parentName="p">{`Team Management`}</strong>{`.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`On the `}<strong parentName="p">{`Teams`}</strong>{` tab, click the team whose user you want to remove.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`On the `}<strong parentName="p">{`Members`}</strong>{` tab, select the checkbox of the user you want to remove.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Next to `}<strong parentName="p">{`User Selected`}</strong>{`, in the `}<strong parentName="p">{`Action`}</strong>{` dropdown, click `}<strong parentName="p">{`Remove User from team`}</strong>{`.`}</p>
        <img src={useBaseUrl('img/team-mgmt/remove-user-from-team.jpg')} alt="Remove a user from a team" />
      </li>
    </ol>
    <p>{`If a user is removed from a team and/or not assigned to a team, their name will only be visible in the `}<strong parentName="p">{`Users`}</strong>{` tab.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
