  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'installation',
	title: 'Testrunner Installation',
	sidebar_label: 'Installation'
};
export const metadata = {
  "unversionedId": "products/testrunner-toolkit/installation",
  "id": "products/testrunner-toolkit/installation",
  "isDocsHomePage": false,
  "title": "Testrunner Installation",
  "description": "Sauce Labs Testrunner Toolkit is a containerized testing solution that simplifies user setup, speeds up test execution time and supports native Javascript frameworks like Puppeteer, Playwright, and TestCafe for running end-to-end web tests with Sauce Labs.",
  "source": "@site/docs/products/testrunner-toolkit/installation.md",
  "permalink": "/products/testrunner-toolkit/installation",
  "editUrl": "https://github.com/saucelabs/sauce-docs/edit/master/docs/products/testrunner-toolkit/installation.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar_label": "Installation",
  "sidebar": "docs",
  "previous": {
    "title": "Why Testrunner Toolkit?",
    "permalink": "/products/testrunner-toolkit"
  },
  "next": {
    "title": "Testrunner Test Preparation",
    "permalink": "/products/testrunner-toolkit/preparation"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'What You&#39;ll Need',
  id: 'what-youll-need',
  children: []
}, {
  value: 'Installing Testrunner Toolkit',
  id: 'installing-testrunner-toolkit',
  children: []
}, {
  value: 'Connecting to Sauce Labs',
  id: 'connecting-to-sauce-labs',
  children: []
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Sauce Labs Testrunner Toolkit is a containerized testing solution that simplifies user setup, speeds up test execution time and supports native Javascript frameworks like `}<a parentName="p" {...{
        "href": "https://developers.google.com/web/tools/puppeteer"
      }}>{`Puppeteer`}</a>{`, `}<a parentName="p" {...{
        "href": "https://playwright.dev/"
      }}>{`Playwright`}</a>{`, and `}<a parentName="p" {...{
        "href": "https://devexpress.github.io/testcafe/"
      }}>{`TestCafe`}</a>{` for running end-to-end web tests with `}<a parentName="p" {...{
        "href": "https://saucelabs.com/"
      }}>{`Sauce Labs`}</a>{`.`}</p>
    <h2 {...{
      "id": "what-youll-need"
    }}>{`What You'll Need`}</h2>
    <ul>
      <li parentName="ul">{`A `}<a parentName="li" {...{
          "href": "https://saucelabs.com/"
        }}>{`Sauce Labs`}</a>{` account (if you don't have one, start a `}<a parentName="li" {...{
          "href": "https://saucelabs.com/sign-up"
        }}>{`free trial`}</a>{`)`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://docs.docker.com/get-docker/"
        }}>{`Docker`}</a>{` installed`}</li>
      <li parentName="ul">{`Ensure the `}<a parentName="li" {...{
          "href": "https://docs.docker.com/config/daemon/"
        }}>{`Docker daemon`}</a>{` is running (e.g. `}<inlineCode parentName="li">{`docker info`}</inlineCode>{` works in your terminal)`}</li>
    </ul>
    <h2 {...{
      "id": "installing-testrunner-toolkit"
    }}>{`Installing Testrunner Toolkit`}</h2>
    <p>{`There are multiple ways to install the Sauce Labs Testrunner Toolkit (colloquially known as `}<inlineCode parentName="p">{`saucectl`}</inlineCode>{`):`}</p>
    <ul>
      <li parentName="ul">{`Use `}<a parentName="li" {...{
          "href": "https://www.npmjs.com/"
        }}>{`NPM`}</a><pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash"
          }}>{`npm i -g saucectl
`}</code></pre></li>
      <li parentName="ul">{`Use our one line installer via command line tools such as `}<a parentName="li" {...{
          "href": ""
        }}>{`cURL`}</a>{`:`}<pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash"
          }}>{`curl -L https://saucelabs.github.io/saucectl/install | bash
`}</code></pre></li>
    </ul>
    <p>{`If you would like to inspect the content of our one line installer, download it, have a look, and execute it:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`curl -fsSL -o get_saucectl.sh https://saucelabs.github.io/saucectl/install \\
chmod 700 get_saucectl.sh \\
./get_saucectl.sh
`}</code></pre>
    <h2 {...{
      "id": "connecting-to-sauce-labs"
    }}>{`Connecting to Sauce Labs`}</h2>
    <p>{`You need to retrieve your Sauce Labs `}<inlineCode parentName="p">{`username`}</inlineCode>{` and `}<inlineCode parentName="p">{`accessKey`}</inlineCode>{` in order post your test results. To retireve your Sauce Labs account credentials navigate to `}<a parentName="p" {...{
        "href": "https://app.saucelabs.com/user-settings"
      }}>{`Account > User Settings`}</a>{`.`}</p>
    <p>{`Sauce Labs recommends as a best practice to set your account credentials as environment variables like so:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`SAUCE_USERNAME='valid.username'
SAUCE_ACCESS_KEY='valid.key'
`}</code></pre>
    <p>{`For specific instructions on how to set environment variables visit, the following links:`}</p>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://www.architectryan.com/2018/08/31/how-to-change-environment-variables-on-windows-10/"
        }}>{`Set Environment Variables with Windows 10`}</a>{` `}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://apple.stackexchange.com/questions/106778/how-do-i-set-environment-variables-on-os-x"
        }}>{`Set Environment Variables with MacOS`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://askubuntu.com/questions/58814/how-do-i-add-environment-variables"
        }}>{`Set Environment Variables with Linux`}</a></li>
    </ul>
    <blockquote>
      <p parentName="blockquote">{`If you are using a cloud CI/CD tool, we strongly suggest protecting these values through secrets or context variables.`}</p>
    </blockquote>
    <hr></hr>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
