import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
export const rightToc = [{
  value: 'What You&#39;ll Need',
  id: 'what-youll-need',
  children: []
}, {
  value: 'Upload an App with App-Upload',
  id: 'upload-an-app-with-app-upload',
  children: [{
    value: 'Delete Apps with the Delete Button',
    id: 'delete-apps-with-the-delete-button',
